INSERT INTO `l2` (`id`, `globalId`, `question`, `answers`, `pravila`, `correctAnswer`, `glava`) VALUES
(1, 186875, 'На кого распространяются Правила по охране труда при эксплуатации электроустановок?', '[\"На работников промышленных предприятий, в составе которых имеются электроустановки\",\"На работников организаций независимо от форм собственности и организационно-правовых форм и других физических лиц, занятых техническим обслуживанием электроустановок, проводящих в них оперативные переключения, организующих и выполняющих испытания и измерения\",\"На работодателей - юридических и физических лиц независимо от их организационно-правовых форм и работников из числа электротехнического, электротехнологического и неэлектротехнического персонала\",\"На работников всех организаций независимо от формы собственности, занятых техническим обслуживанием электроустановок и выполняющих в них строительные, монтажные и ремонтные работы\"]', '', 3, ''),
(2, 186876, 'В каком случае удостоверение о проверке знаний правил работы в электроустановках подлежит замене?', '[\"По истечении срока действия группы по электробезопасности\",\"В случае утери удостоверения\",\"При повышении группы по электробезопасности\",\"В случае изменения должности\"]', '', 4, ''),
(3, 186877, 'Когда проводятся внеочередные осмотры воздушной линии электропередачи?', '[\"Только при образовании на проводах и тросах гололеда, после сильных бурь, ураганов и других стихийных бедствий, во время ледохода и разлива рек\",\"Только при пляске проводов\",\"Только при пожарах в зоне трассы ВЛ\",\"Только после отключения ВЛ релейной защитой и неуспешного автоматического повторного включения\",\"В любом из перечисленных случаев\"]', '', 5, ''),
(4, 186878, 'Допускается ли прохождение воздушной линии электропередачи (ВЛ) по территории стадионов, учебных и детских учреждений?', '[\"Не допускается\",\"Допускается\",\"Допускается при согласовании с территориальными органами Ростехнадзора\",\"Допускается при соответствующем обосновании в рабочей документации\"]', '', 1, ''),
(5, 186879, 'Когда должна осуществляться подготовка персонала для вводимых в работу новых и реконструируемых объектов электроэнергетики?', '[\"За полгода до ввода в эксплуатацию\",\"До начала проведения пробных пусков и комплексного опробования оборудования\",\"За год до ввода в эксплуатацию\"]', '', 2, ''),
(6, 186880, 'При каком условии работники, не обслуживающие электроустановки, могут допускаться в РУ до 1000 В?', '[\"В сопровождении оперативного персонала, обслуживающего данную электроустановку, имеющего группу IV, либо работника, имеющего право единоличного осмотра\",\"В сопровождении оперативного персонала, обслуживающего данную электроустановку, имеющего группу III, либо работника, имеющего право единоличного осмотра\",\"В сопровождении опытного работника из числа ремонтного персонала, имеющего группу по электробезопасности не ниже V\"]', '', 2, ''),
(7, 186881, 'Что является подтверждением проведения и получения целевого инструктажа членами бригады?', '[\"Подписи членов бригады в таблицах регистрации целевых инструктажей\",\"Подписи ответственного руководителя работ в таблицах регистрации целевых инструктажей\",\"Запись в таблице регистрации целевого инструктажа\"]', '', 1, ''),
(8, 186882, 'Каким образом производится присоединение заземляющих проводников к заземлителю и заземляющим конструкциям?', '[\"Сваркой\",\"Болтовым соединением\",\"Резьбовым соединением\",\"Фланцевым соединением\"]', '', 1, ''),
(9, 186883, 'Какое напряжение, согласно Правилам устройства электроустановок, должно применяться для питания переносных светильников в помещениях с повышенной опасностью и особо опасных помещениях?', '[\"Напряжение не выше 50 В\",\"Напряжение не выше 110 В\",\"Напряжение не выше 220 В\",\"Напряжение не выше 127 В\"]', '', 1, ''),
(10, 186884, 'На кого не распространяются требования \"Правил работы с персоналом в организациях электроэнергетики Российской Федерации\"?', '[\"Системного оператора и субъекты оперативно-диспетчерского управления в электроэнергетике в технологически изолированных территориальных электроэнергетических системах (далее - субъекты оперативно-диспетчерского управления)\",\"Субъекты электроэнергетики и потребителей электрической энергии, владеющих на праве собственности или ином законном основании объектами по производству электрической энергии, в том числе объектами, функционирующими в режиме комбинированной выработки электрической и тепловой энергии\",\"Субъекты электроэнергетики и потребителей электрической энергии, владеющих на праве собственности или ином законном основании объектами электросетевого хозяйства\",\"Потребителей электрической энергии и владельцев объектов электроэнергетики, являющихся физическими лицами\"]', '', 4, ''),
(11, 186885, 'В каких целях допускается приближение на расстояние менее 8 метров к месту возникновения короткого замыкания на землю при работах на воздушной линии электропередачи?', '[\"Только для оказания доврачебной помощи людям, попавшим под напряжение\",\"Только для определения визуального расстояния до опоры воздушной линии\",\"Только для оперативных переключений с целью ликвидации замыкания и освобождения людей, попавших под напряжение\"]', '', 3, ''),
(12, 186886, 'Какая электроустановка считается действующей?', '[\"Исправная электроустановка\",\"Электроустановка или ее часть, которая находится под напряжением, либо на которую напряжение может быть подано включением коммутационных аппаратов\",\"Электроустановка, которая находится в постоянной эксплуатации\",\"Электроустановка, которая находится под напряжением не ниже 220 В\"]', '', 2, ''),
(13, 186887, 'В какой цвет должны быть окрашены открыто проложенные заземляющие проводники?', '[\"В синий цвет\",\"В зеленый цвет\",\"В черный цвет\",\"В красный цвет\"]', '', 3, ''),
(14, 186888, 'Для чего, согласно Правилам устройства электроустановок, предназначено освещение безопасности?', '[\"Для продолжения работы при аварийном отключении рабочего освещения\",\"Для освещения территории в нерабочее время\",\"Для установки вдоль границ территорий, охраняемых специальным персоналом\",\"Для обеспечения освещения вне производственных помещений\"]', '', 1, ''),
(15, 186889, 'Кто утверждает порядок проведения работы с персоналом в организации?', '[\"Руководитель организации или уполномоченное им должностное лицо организации\",\"Инспектор Ростехнадзора\",\"Ответственный за электрохозяйство Потребителя\",\"Федеральный орган исполнительной власти\"]', '', 1, ''),
(16, 186890, 'Кто дает разрешение на снятие напряжения при несчастных случаях для освобождения пострадавшего от действия электрического тока?', '[\"Разрешение дает оперативный персонал энергообъекта\",\"Разрешение дает вышестоящий оперативный персонал\",\"Разрешение дает административно-технический персонал\",\"Предварительного разрешения оперативного персонала не требуется. Напряжение должно быть снято немедленно\"]', '', 4, ''),
(17, 186891, 'Что является определением термина \"инструктаж целевой\"?', '[\"Указания по безопасному выполнению конкретной работы в электроустановке, охватывающие категорию работников, определенных нарядом или распоряжением, от выдавшего наряд, отдавшего распоряжение до члена бригады или исполнителя\",\"Указания по безопасному выполнению конкретной работы в электроустановке для членов бригады или исполнителей\",\"Указания по безопасному выполнению разовых работ, не связанных с прямыми должностными обязанностями по специальности\"]', '', 1, ''),
(18, 186892, 'Какое напряжение должно применяться для питания переносных (ручных) светильников, применяемых в помещениях с повышенной опасностью?', '[\"Не выше 12 В\",\"Не выше 42 В\",\"Не выше 50 В\",\"Не выше 127 В\"]', '', 3, ''),
(19, 186893, 'Укажите последовательность действий по оценке обстановки и обеспечению безопасных условий для оказания первой помощи (приказ Минздрава России от 04.05.2012 N 477н).', '[\"1) Определение угрожающих факторов для жизни и здоровья пострадавшего; 2) устранение угрожающих факторов для жизни и здоровья; 3) вызов скорой медицинской помощи;4) прекращение действия повреждающих факторов на пострадавшего; 5) оценка количества пострадавших\",\"1) Определение угрожающих факторов для собственной жизни и здоровья; 2) определение угрожающих факторов для жизни и здоровья пострадавшего; 3) устранение угрожающих факторов для жизни и здоровья; 4) прекращение действия повреждающих факторов на пострадавшего; 5) оценка количества пострадавших; 6) извлечение пострадавшего из транспортного средства или других труднодоступных мест; 7) перемещение пострадавшего\",\"1) Вызов скорой медицинской помощи; 2) других специальных служб, сотрудники которых обязаны оказывать первую помощь в соответствии с федеральным законом или со специальным правилом; 3) устранение угрожающих факторов для жизни и здоровья; 4) прекращение действия повреждающих факторов на пострадавшего; 5) оценка количества пострадавших; 6) извлечение пострадавшего из транспортного средства или других труднодоступных мест\"]', '', 2, ''),
(20, 186894, 'Какой персонал не проходит подготовку по новой должности (рабочему месту)?', '[\"Диспетчерский персонал\",\"Оперативный персонал\",\"Оперативно-ремонтный персонал\",\"Ремонтный персонал\",\"Административно-технический персонал\"]', '', 5, ''),
(21, 186895, 'Каким образом не допускается производство работ в действующих электроустановках?', '[\"По наряду-допуску\",\"По распоряжению\",\"На основании перечня работ, выполняемых в порядке текущей эксплуатации\",\"Самовольно\"]', '', 4, ''),
(22, 186896, 'Чем должны быть укомплектованы электроустановки?', '[\"Защитными средствами, средствами пожаротушения\",\"Средствами пожаротушения, исправным инструментом и средствами оказания первой медицинской помощи\",\"Исправным инструментом\",\"Испытанными защитными средствами, средствами пожаротушения, исправным инструментом и средствами оказания первой помощи\"]', '', 4, ''),
(23, 186897, 'Какие помещения относятся к помещениям с повышенной опасностью поражения людей электрическим током?', '[\"Помещения, характеризующиеся наличием сырости или токопроводящей пыли\",\"Помещения, характеризующиеся наличием металлических, земляных, железобетонных и других токопроводящих полов\",\"Помещения, характеризующиеся наличием высокой температуры\",\"Помещения, характеризующиеся возможностью одновременного прикосновения человека к металлоконструкциям зданий, имеющим соединение с землей, технологическим аппаратам, механизмам и т. п. с одной стороны, и к металлическим корпусам электрооборудования (открытым проводящим частям) - с другой\",\"Любое из перечисленных помещений относится к помещениям с повышенной опасностью\"]', '', 5, ''),
(24, 186898, 'Укажите верный перечень исчерпывающих мероприятий по оказанию первой помощи. (приказ Минздрава России от 04.05.2012 N 477н).', '[\"1) Оценка обстановки и обеспечение безопасных условий для оказания первой помощи;\\n2) вызов скорой медицинской помощи, других специальных служб, сотрудники которых обязаны оказывать первую помощь;\\n3) определение наличия сознания у пострадавшего;\\n4) восстановление проходимости дыхательных путей и определение признаков жизни у пострадавшего;\\n5) проведение сердечно-легочной реанимации до появления признаков жизни;\\n6) поддержание проходимости дыхательных путей;\\n7) осмотр пострадавшего и временная остановка наружного кровотечения;\\n8) подробный осмотр пострадавшего в целях выявления признаков травм, отравлений и других состояний, угрожающих его жизни и здоровью и оказание первой помощи;\\n9) придание пострадавшему оптимального положения тела;\\n10) контроль состояния пострадавшего (сознание, дыхание, кровообращение) и оказание психологической поддержки;\\n11) передача пострадавшего бригаде скорой медицинской помощи, другим специальным службам, сотрудники которых обязаны оказывать первую помощь\",\"1) Вызов скорой медицинской помощи, других специальных служб, сотрудники которых обязаны оказывать первую помощь;\\n2) определение наличия сознания у пострадавшего;\\n3) восстановление проходимости дыхательных путей и определение признаков жизни у пострадавшего;\\n4) проведение сердечно-легочной реанимации до появления признаков жизни;\\n5) осмотр пострадавшего и временная остановка наружного кровотечения;\\n6) придание пострадавшему оптимального положения тела;\\n7) передача пострадавшего бригаде скорой медицинской помощи, другим специальным службам, сотрудники которых обязаны оказывать первую помощь\",\"1) Убедиться в отсутствии пульса на сонной артерии;\\n2) убедиться в отсутствии признаков дыхания;\\n3) освободить грудную клетку от одежды и расстегнуть поясной ремень;\\n4) прикрыть двумя пальцами мечевидный отросток;\\n5) нанести удар кулаком по грудине;\\n6) проверить пульс, при отсутствии пульса перейти к непрямому массажу сердца\"]', '', 1, ''),
(25, 186899, 'В каком объеме должна проводиться стажировка для диспетчерского, оперативного, оперативно-ремонтного и ремонтного персонала?', '[\"В объеме, определенном программой подготовки по новой должности\",\"В объеме, 72 академических часов\",\"В объеме, 160 академических часов\",\"В объеме, 240 академических часов\"]', '', 1, ''),
(26, 186900, 'Допускается ли самовольное проведение работ в действующих электроустановках, а также расширение рабочих мест и объема задания, определенных наряд-допуском, распоряжением или утвержденным работодателем перечнем работ, выполняемых в порядке текущей эксплуатации?', '[\"Допускается самовольное проведение работ только при возникновении аварийной ситуации с разрешения вышестоящего оперативного персонала\",\"Допускается расширение рабочих мест и объема задания, определенных наряд-допуском или распоряжением при выполнении неотложных работ, для выполнения которых требуется более 1 часа, с разрешения производителя работ\",\"Допускается, учитывая важность электроустановки в технологическом процессе, с обязательной записью в оперативном журнале\",\"Самовольное проведение работ, расширение рабочих мест и объема задания, определенных наряд-допуском, распоряжением или утвержденным работодателем перечнем работ, выполняемых в порядке текущей эксплуатации в действующих электроустановках не допускается\"]', '', 4, ''),
(27, 186901, 'За что несут персональную ответственность работники, непосредственно обслуживающие электроустановки?', '[\"За несвоевременное и неудовлетворительное техническое обслуживание электроустановок\",\"За нарушения, происшедшие по их вине, а также за неправильную ликвидацию ими нарушений в работе электроустановок на обслуживаемом участке\",\"За нарушения в работе, вызванные низким качеством ремонта\",\"За нарушения в эксплуатации электротехнологического оборудования\"]', '', 2, ''),
(28, 186902, 'Какие помещения, согласно ПУЭ, называются сырыми?', '[\"Помещения, в которых относительная влажность воздуха не превышает 60 %\",\"Помещения, в которых относительная влажность воздуха превышает 75 %\",\"Помещения, в которых относительная влажность воздуха не превышает 90 %\",\"Помещения, в которых относительная влажность воздуха близка к 100 %\"]', '', 2, ''),
(29, 186903, 'Укажите последовательность действий по восстановлению проходимости дыхательных путей и определению признаков жизни у пострадавшего. (приказ Минздрава России от 04.05.2012 N 477н).', '[\"1) Удалить слизь и содержимое желудка;\\n2) приподнять ноги и расстегнуть поясной ремень, при возможности положить холод на живот\",\"1) Запрокинуть голову с подъемом подбородка;\\n2) выдвинуть нижнюю челюсть;\\n3) определить наличие дыхания с помощью слуха, зрения и осязания;\\n4) определить наличие кровообращения, проверить пульс на магистральных артериях\",\"1) Убедиться в отсутствии пульса на сонной артерии;\\n2) убедиться в отсутствии признаков дыхания;\\n3) освободить грудную клетку от одежды и расстегнуть поясной ремень\"]', '', 2, ''),
(30, 186904, 'Какие рабочие места предусмотрены во время стажировки для оперативного, оперативно-ремонтного персонала?', '[\"Свое рабочее место и (или) рабочие места оперативного персонала объектов электроэнергетики такой организации, определенные программой подготовки по новой должности\",\"Рабочее место в своем диспетчерском центре, а также рабочее место диспетчера в полнофункциональном резервном диспетчерском центре (при его наличии)\",\"Свое рабочее место и (или) специально оборудованные полигоны и мастерские\"]', '', 1, ''),
(31, 186905, 'Допустимо ли пребывание одного или нескольких членов бригады отдельно от производителя работ, в случае рассредоточения членов бригады по разным рабочим местам?', '[\"Недопустимо в любом случае\",\"Допустимо в любом случае\",\"Допустимо при наличии у членов бригады III группы по электробезопасности\",\"Допустимо при проведении соответствующего инструктажа\"]', '', 3, ''),
(32, 186906, 'Какая ответственность предусмотрена за нарушение Правил технической эксплуатации электроустановок потребителей?', '[\"Уголовная\",\"Административная\",\"Дисциплинарная\",\"В соответствии с действующим законодательством Российской Федерации\"]', '', 4, ''),
(33, 186907, 'Какие помещения, согласно ПУЭ, относятся к влажным?', '[\"Помещения, в которых относительная влажность воздуха больше 60 %, но не превышает 75 %\",\"Помещения, в которых относительная влажность воздуха в пределах 80 %\",\"Помещения, в которых относительная влажность воздуха больше 75 %, но не превышает 90 %\",\"Помещения, в которых относительная влажность воздуха близка к 100 %\"]', '', 1, ''),
(34, 186908, 'Перечень состояний при которых не оказывается первая помощь в соответствии с приказом Минздрава России от 04.05.2012 N 477н?', '[\"Отсутствие сознания, остановка дыхания и кровообращения\",\"Наружные кровотечения, травмы различных областей тела\",\"Степень сильного алкогольного опьянения, нарушение координации\"]', '', 3, ''),
(35, 186909, 'Какие рабочие места предусмотрены во время стажировки для ремонтного персонала?', '[\"Свое рабочее место и (или) рабочие места оперативного персонала объектов электроэнергетики такой организации, определенные программой подготовки по новой должности\",\"Рабочее место в своем диспетчерском центре, а также рабочее место диспетчера в полнофункциональном резервном диспетчерском центре (при его наличии)\",\"Свое рабочее место и (или) специально оборудованные полигоны и мастерские\"]', '', 3, ''),
(36, 186910, 'В каких электроустановках могут выполняться работы в порядке текущей эксплуатации?', '[\"В электроустановках напряжением до 1000 В\",\"В электроустановках напряжением до и выше 1000 В\",\"В любых электроустановках\",\"В электроустановках напряжением не выше 380 В\"]', '', 1, ''),
(37, 186911, 'Можно ли принимать в эксплуатацию электроустановки с дефектами и недоделками?', '[\"Можно, с условием устранения недоделок в течение месяца со дня приемки электроустановки в эксплуатацию\",\"Можно, если на это есть разрешение энергонадзора\",\"Можно, если имеющиеся дефекты не влияют на работу электроустановки\",\"Приемка в эксплуатацию электроустановок с недоделками не допускается\"]', '', 4, ''),
(38, 186912, 'Какие помещения, согласно ПУЭ, называются сухими?', '[\"Помещения, в которых относительная влажность воздуха не превышает 60 %\",\"Помещения, в которых относительная влажность воздуха не превышает 75 %\",\"Помещения, в которых относительная влажность воздуха не превышает 70 %\",\"Помещения, в которых относительная влажность воздуха близка к 65 %\"]', '', 1, ''),
(39, 186913, 'Перечислите мероприятия по проведению сердечно-легочной реанимации до появления признаков жизни. (приказ Минздрава России от 04.05.2012 N 477н).', '[\"1) Давление руками на грудину пострадавшего;\\n2) искусственное дыхание «Рот ко рту» (»Рот к носу», с использованием устройства для искусственного дыхания)\",\"1) Освободить грудную клетку от одежды и расстегнуть поясной ремень;\\n2) прикрыть двумя пальцами мечевидный отросток;\\n3) нанести удар кулаком по грудине;\\n4) проверить пульс, при отсутствии пульса перейти к непрямому массажу сердца\",\"1) Убедиться в отсутствии пульса на сонной артерии;\\n2) прикрыть двумя пальцами мечевидный отросток, нанести удар по грудине;\\n3) перейти к непрямому массажу сердца;\\n4) сделать вдох искусственного дыхания\"]', '', 1, ''),
(40, 186914, 'Какие требования в процессе стажировки оперативный, оперативно-ремонтный и ремонтный персонал должен усвоить?', '[\"Только требования отраслевых актов\",\"Только требования инструктивно-технических документов в сфере электроэнергетики\",\"Требования отраслевых актов и инструктивно-технических документов в сфере электроэнергетики и теплоснабжения\"]', '', 3, ''),
(41, 186915, 'Какие работы из перечисленных можно отнести к работам, выполняемым в порядке текущей эксплуатации в электроустановках напряжением до 1000 В?', '[\"Снятие и установку электросчетчиков, других приборов и средств измерений\",\"Ремонт пусковой и коммутационной аппаратуры, установленной на щитках\",\"Замену ламп и чистку светильников на высоте более 2,5 м\",\"Любые из перечисленных работ\"]', '', 1, ''),
(42, 186916, 'На какие категории подразделяется электротехнический персонал организации?', '[\"На административно-технический, оперативный и ремонтный\",\"На оперативный, ремонтный и оперативно-ремонтный\",\"На административно-технический, оперативно-ремонтный, оперативный и ремонтный\",\"На административный, ремонтный и оперативный\"]', '', 3, ''),
(43, 186917, 'Каким образом должны быть обозначены нулевые рабочие (нейтральные) проводники в электроустановках?', '[\"Буквой N и голубым цветом\",\"Буквой N и белым цветом\",\"Буквой Н и голубым цветом\",\"Буквой Н и серым цветом\"]', '', 1, ''),
(44, 186918, 'Какие предпринимаются действия по поддержанию проходимости дыхательных путей? (приказ Минздрава России от 04.05.2012 N 477н).', '[\"1) До приезда скорой помощи периодически делать «вдох» искусственного дыхания;\\n2) приложить холод к голове\",\"1) Придать пострадавшему устойчивое боковое положение;\\n2) запрокинуть голову с подъемом подбородка;\\n3) выдвинуть нижнюю челюсть\",\"1) Положить пострадавшего на живот;\\n2) подогнуть колени;\\n3) вызвать рвотные позывы\"]', '', 2, ''),
(45, 186919, 'Каким образом фиксируются результаты проверки знаний?', '[\"Формируется приказ\",\"Оформляется протоколом проверки знаний и удостоверением и фиксируется в журнале учета проверки знаний\",\"Формируется акт о проведении и получении соответствующих результатов работников\",\"Только удостоверением\"]', '', 2, ''),
(46, 186920, 'Какие из перечисленных мероприятий необходимо учитывать при оформлении перечня работ, выполняемых в порядке текущей эксплуатации?', '[\"Только условия безопасности и возможности единоличного выполнения конкретных работ\",\"Только квалификацию персонала\",\"Только степень важности электроустановки в целом или ее отдельных элементов в технологическом процессе\",\"Необходимо учитывать все перечисленные мероприятия\"]', '', 4, ''),
(47, 186921, 'Какой персонал относится к электротехнологическому?', '[\"Персонал, который проводит ремонт и обслуживание электроустановок\",\"Персонал, который проводит монтаж, наладку и испытание электротехнологического оборудования\",\"Персонал, который проводит обслуживание электротехнологических установок, и использует в работе электрические машины, переносной электроинструмент и светильники\",\"Персонал, который не попадает под определение электротехнического\"]', '', 3, ''),
(48, 186922, 'Каким образом обозначаются проводники защитного заземления, а также нулевые защитные проводники в электроустановках напряжением до 1 кВ с глухозаземленной нейтралью?', '[\"Обозначаются PE и имеют цветовое обозначение чередующимися продольными или поперечными полосами одинаковой ширины желтого и зеленого цветов\",\"Обозначаются RE и имеют цветовое обозначение чередующимися продольными или поперечными полосами одинаковой ширины белого и зеленого цветов\",\"Обозначаются PE и имеют цветовое обозначение чередующимися продольными или поперечными полосами одинаковой ширины желтого и белого цветов\"]', '', 1, ''),
(49, 186923, 'Перечислите мероприятия по обзорному осмотру пострадавшего и временной остановке наружного кровотечения. (приказ Минздрава России от 04.05.2012 N 477н).', '[\"1) Прижать к ране сложенную чистую ткань или бинт;\\n2) уложить пострадавшего в горизонтальное положение;\\n3) использовать пальцевое прижатие раны;\\n4) фиксировать повязку косынкой, платком или шапкой-ушанкой\",\"1) Осмотр пострадавшего на наличие кровотечений;\\n2) проведение осмотра конечностей;\\n3) наложение повязок при травмах различных областей тела;\\n4) придание пострадавшему оптимального положения тела\",\"1) Обзорный осмотр пострадавшего на наличие кровотечений;\\n2) пальцевое прижатие артерии;\\n3) наложение жгута;\\n4) максимальное сгибание конечности в суставе;\\n5) прямое давление на рану;\\n6) наложение давящей повязки\"]', '', 3, ''),
(50, 186924, 'В каких случаях не проводится дублирование?', '[\"При подготовке по новой должности - после проверки знаний\",\"После перерыва в работе более 30 календарных дней, но менее 60 календарных дней - в случаях, установленных порядком проведения работы с персоналом, принятым в организации\",\"После перерыва в работе от 60 календарных дней до 6 месяцев\",\"После перерыва в работе 20 календарных дней до 1 месяца\"]', '', 4, ''),
(51, 186925, 'Кто инструктирует бригаду по вопросам использования инструмента и приспособлений?', '[\"Ответственный руководитель работ\",\"Производитель работ\",\"Допускающий\"]', '', 2, ''),
(52, 186926, 'Какая периодичность проверки знаний по электробезопасности установлена для электротехнического персонала, непосредственно организующего и проводящего работы по обслуживанию действующих электроустановок?', '[\"Не реже одного раза в год\",\"Не реже одного раза в два года\",\"Не реже одного раза в три года\",\"Не реже одного раза в пять лет\"]', '', 1, ''),
(53, 186927, 'Что, согласно Правилам устройства электроустановок, называется приемником электрической энергии (электроприемником)?', '[\"Распределительное устройство, предназначенное для обеспечения потребителей электрической энергией\",\"Подстанция, работающая на определенной территории\",\"Электроустановка, предназначенная для обеспечения потребителей электрической энергией\",\"Аппарат, агрегат и др., предназначенный для преобразования электрической энергии в другой вид энергии\"]', '', 4, ''),
(54, 186928, 'Какие действия оказывающего помощь не относятся к мероприятиям по подробному осмотру пострадавшего в целях выявления признаков травм, отравлений и других состояний, угрожающих его жизни и здоровью, и по оказанию первой помощи в случае выявления указанных состояний (приказ Минздрава России от 04.05.2012 N 477н)?', '[\"1) Осмотр пострадавшего на наличие кровотечений;\\n2) прекращение воздействия опасных химических веществ на пострадавшего проведением клизмы;\\n3) наложение гипсовых повязок при повреждении конечностей;\\n4) охлаждение поврежденных конечностей в течение получаса\",\"1) Проведение осмотра головы;\\n2) проведение осмотра шеи;\\n3) проведение осмотра груди;\\n4) проведение осмотра спины;\\n5) проведение осмотра живота и таза;\\n6) проведение осмотра конечностей;\\n7) наложение повязок при травмах различных областей тела, в том числе окклюзионной (герметизирующей) при ранении грудной клетки\",\"1) Проведение иммобилизации;\\n2) прекращение воздействия опасных химических веществ на пострадавшего (промывание желудка путем приема воды и вызывания рвоты, удаление с поврежденной поверхности и промывание поврежденной поверхности проточной водой);\\n3) местное охлаждение при травмах, термических ожогах и иных воздействиях высоких температур или теплового излучения;\\n4) термоизоляция при отморожениях и других эффектах воздействия низких температур\"]', '', 1, ''),
(55, 186929, 'Кто несет ответственность за действия работника, допущенного к дублированию на рабочем месте?', '[\"Сам работник, допущенный к дублированию, так и работник, под руководством и контролем которого проводится дублирование\",\"Только сам работник\",\"Руководитель организации\",\"Инспектор Ростехнадзора\"]', '', 1, ''),
(56, 186930, 'Как классифицируются электроинструмент и ручные электрические машины по способу защиты от поражения электрическим током?', '[\"Делятся на 4 класса - нулевой, первый, второй и третий\",\"Делятся на 3 класса - первый, второй и третий\",\"Делятся на 4 класса - первый, второй, третий и четвертый\",\"Делятся на 3 класса - нулевой, первый и второй\"]', '', 1, ''),
(57, 186931, 'Сколько человек должно быть в комиссии по проверке знаний электротехнического персонала?', '[\"Не менее трех человек\",\"Не менее четырех человек\",\"Не менее пяти человек\",\"Правилами не регламентируется\"]', '', 3, ''),
(58, 186932, 'Что является определением термина \"защита от прямого прикосновения\"?', '[\"Защита от поражения электрическим током при прикосновении к открытым проводящим частям, оказавшимся под напряжением, при повреждении изоляции\",\"Защита людей или животных от электрического контакта с открытыми проводящими частями\",\"Защита для предотвращения прикосновения к токоведущим частям, находящимся под напряжением\"]', '', 3, ''),
(59, 186933, 'На каком этапе производится вызов скорой медицинской помощи, других специальных служб, сотрудники которых обязаны оказывать первую помощь в соответствии с федеральным законом или со специальным правилом согласно приказу Минздрава России от 04.05.2012 N 477н?', '[\"По окончании проведения сердечно-легочной реанимации и при появлении признаков жизни\",\"После обнаружения пострадавшего и оценки обстановки по обеспечению безопасных условий для оказания первой помощи\",\"После осмотра пострадавшего и временной остановки наружного кровотечения\"]', '', 2, ''),
(60, 186934, 'Какое количество противоаварийных тренировок необходимо за время дублирования?', '[\"Не менее 10 противоаварийных тренировок\",\"Определяются индивидуальной программой подготовки по новой должности в соответствии с Правилами проведения противоаварийных тренировок\",\"Не менее 5 противоаварийных тренировок\",\"Не менее 7 противоаварийных тренировок\"]', '', 2, ''),
(61, 186935, 'Что запрещено работнику при выполнении работ с применением переносного электроинструмента?', '[\"Выполнять тестирование устройства защитного отключения\",\"Проверять комплектность и надежность крепления деталей\",\"Проверять исправность цепи заземления у машин I класса\",\"Разбирать ручные электрические машины и электроинструмент, производить какой-либо ремонт\"]', '', 4, ''),
(62, 186936, 'Каким образом оформляются результаты проверки знаний персонала по электробезопасности?', '[\"Результаты проверки заносятся в журнал установленной формы, персоналу, успешно прошедшему проверку знаний, выдается удостоверение установленной формы\",\"Результаты проверки заносятся в журнал установленной формы\",\"Результаты проверки оформляются протоколом установленной формы, персоналу, успешно прошедшему проверку знаний, выдается удостоверение установленной формы\",\"Результаты проверки заносятся в трудовую книжку и в удостоверение установленной формы, которое выдается персоналу, успешно прошедшему проверку знаний\"]', '', 1, ''),
(63, 186937, 'Что является определением термина \"защита при косвенном прикосновении\"?', '[\"Защита от поражения электрическим током при прикосновении к открытым проводящим частям, оказавшимся под напряжением, при повреждении изоляции\",\"Защита от напряжения, возникающего при стекании тока с заземлителя в землю, между точкой ввода тока в заземлитель и зоной нулевого потенциала\",\"Защита для предотвращения прикосновения к токоведущим частям, находящимся под напряжением\"]', '', 1, ''),
(64, 186938, 'Укажите верный перечень исчерпывающих мероприятий по оказанию первой помощи (приказ Минздрава России от 04.05.2012 N 477н).', '[\"1) Определение угрожающих факторов для жизни и здоровья пострадавшего;\\n2) устранение угрожающих факторов для жизни и здоровья;\\n3) вызов скорой медицинской помощи;\\n4) прекращение действия повреждающих факторов на пострадавшего;\\n5) оценка количества пострадавших\",\"1) Определение угрожающих факторов для собственной жизни и здоровья;\\n2) определение угрожающих факторов для жизни и здоровья пострадавшего;\\n3) устранение угрожающих факторов для жизни и здоровья;\\n4) прекращение действия повреждающих факторов на пострадавшего;\\n5) оценка количества пострадавших;\\n6) извлечение пострадавшего из транспортного средства или других труднодоступных мест;\\n7) перемещение пострадавшего\",\"1) Вызов скорой медицинской помощи;\\n2) других специальных служб, сотрудники которых обязаны оказывать первую помощь в соответствии с федеральным законом или со специальным правилом;\\n3) устранение угрожающих факторов для жизни и здоровья;\\n4) прекращение действия повреждающих факторов на пострадавшего;\\n5) оценка количества пострадавших;\\n6) извлечение пострадавшего из транспортного средства или других труднодоступных мест\"]', '', 2, ''),
(65, 186939, 'Какие ознакомительные мероприятия проводятся перед допуском к самостоятельной работе персонала, имевшего перерыв в работе, независимо от проводимых форм подготовки в соответствии с занимаемой должностью?', '[\"Ознакомлен с изменениями в оборудовании, схемах и режимах работы энергоустановок\",\"Ознакомлен с введенными в действие (вступившими в силу) новыми отраслевыми актами и инструктивно-техническими документами в сфере электроэнергетики (оперативный, оперативно-ремонтный и ремонтный персонал- также в сфере теплоснабжения), являющимися обязательными для использования в работе и исполнения согласно должностным обязанностям (трудовым функциям) работника\",\"Ознакомлен с изменениями, внесенными в действующие отраслевые акты и инструктивно-технические документы в сфере электроэнергетики (оперативный, оперативно-ремонтный и ремонтный персонал, - также в сфере теплоснабжения), являющиеся обязательными для использования в работе и исполнения согласно должностным обязанностям (трудовым функциям) работника\",\"Все перечисленное\"]', '', 4, ''),
(66, 186940, 'Какие требования предъявляются к командированному персоналу?', '[\"Командируемый персонал должен иметь профессиональную подготовку\",\"Командируемый персонал должен иметь удостоверения о проверке знаний правил работы в электроустановках с отметкой о группе по электробезопасности\",\"Командируемый персонал должен быть обучен и аттестован по охране труда и промышленной безопасности, если это необходимо\",\"Командируемый персонал должен пройти предварительное медицинское обследование\"]', '', 2, ''),
(67, 186941, 'Чем должны отличаться светильники аварийного освещения от светильников рабочего освещения?', '[\"Исполнением\",\"Знаками или окраской\",\"Принципиальных отличий нет\"]', '', 2, ''),
(68, 186942, 'Что является определением термина \"заземлитель\"?', '[\"Проводящая часть, не являющаяся частью электроустановки\",\"Проводящая часть или совокупность соединенных между собой проводящих частей, находящихся в электрическом контакте с землей непосредственно или через промежуточную проводящую среду\",\"Сторонняя проводящая часть, находящаяся в электрическом контакте с землей непосредственно или через промежуточную проводящую среду, используемая для целей заземления\"]', '', 2, ''),
(69, 186943, 'В какой срок лицо, получившее неудовлетворительную оценку по результатам проверки знаний, должно пройти повторную проверку?', '[\"Не более 1 месяца\",\"Не позднее 2 недель\",\"Вопрос о сроках повторной проверки и о возможности сохранения трудового договора с работником решается руководителем организации\"]', '', 1, ''),
(70, 186944, 'Какие вопросы включает программа планового производственного инструктажа?', '[\"Особенности и режимы эксплуатации оборудования в период его пуска, нормальной работы, останова, консервации\",\"Принципы работы оборудования и устройств (комплексов) РЗА\",\"Вопросы управления электроэнергетическим режимом и иные технологические вопросы\",\"Все перечисленное\"]', '', 4, ''),
(71, 186945, 'Что должен пройти командированный персонал по прибытии на место своей командировки для выполнения работ в действующих электроустановках?', '[\"Индивидуальную теоретическую подготовку\",\"Контрольную противоаварийную тренировку\",\"Вводный и первичный инструктажи по охране труда\",\"Ознакомление с текущими распорядительными документами организации по вопросам аварийности и травматизма\"]', '', 3, ''),
(72, 186946, 'Какая проверка знаний проводится у персонала при назначении или переводе на другую работу, если новые обязанности требуют дополнительных знаний норм и правил?', '[\"Первичная\",\"Повторная\",\"Очередная\",\"Внеочередная\"]', '', 4, ''),
(73, 186947, 'Что является определением термина \"заземление\"?', '[\"Сторонняя проводящая часть, находящаяся в электрическом контакте с землей непосредственно или через промежуточную проводящую среду, используемая для целей заземления\",\"Заземление точек токоведущих частей электроустановки, выполняемое для обеспечения работы электроустановки\",\"Преднамеренное электрическое соединение какой-либо точки сети, электроустановки или оборудования с заземляющим устройством\"]', '', 3, ''),
(74, 186948, 'Каков порядок допуска к самостоятельной работе вновь принятых работников или имевших перерыв в работе более 6 месяцев?', '[\"В зависимости от категории персонала, после прохождения этапов подготовки в соответствии с индивидуальной программой подготовки\",\"В зависимости от категории персонала, после прохождения специальной подготовки, программу и порядок проведения которой определяет руководитель организации\",\"В зависимости от категории персонала, после ознакомления с изменениями в схемах и режимах работы энергоустановок, с вновь введенными в действие HТД, приказами и распоряжениями\"]', '', 1, ''),
(75, 186949, 'В каких случаях проводится внеплановый производственный инструктаж?', '[\"При принятии новой или внесении изменений в действующую инструктивно-техническую документацию диспетчерских центров субъекта оперативно-диспетчерского управления, инструктивную документацию организации по вопросам\",\"При внесении изменений в действующие отраслевые акты в сфере электроэнергетики (для персонала объектов по производству электрической энергии, функционирующих в режиме комбинированной выработки электрической и тепловой энергии, - также в сфере теплоснабжения), являющиеся обязательными для использования в работе и исполнения согласно должностным обязанностям (трудовым функциям) работника\",\"По решению руководителя или иного уполномоченного должностного лица организации (ее филиала, представительства) при установлении нарушений работниками требований отраслевых актов и (или) инструктивно-технических документов\",\"В случае непрохождения работником планового производственного инструктажа (по темам пропущенного инструктажа\",\"Во всех перечисленных случаях\"]', '', 5, ''),
(76, 186950, 'Кто проводит первичный инструктаж командированному персоналу при проведении работ в электроустановках до 1000 В?', '[\"Работник организации - владельца электроустановок из числа административно-технического персонала, имеющий группу IV\",\"Работник организации - владельца электроустановок из числа электротехнического персонала, имеющий группу IV\",\"Работник организации - владельца электроустановок из числа оперативно-ремонтного персонала, имеющий группу IV\",\"Работник командирующей организации из числа административно-технического персонала, имеющий группу IV\"]', '', 1, ''),
(77, 186951, 'Когда проводится внеочередная проверка знаний персонала?', '[\"При введении в действие у Потребителя новых или переработанных норм и правил\",\"По требованию органов государственного надзора и контроля\",\"При проверке знаний после получения неудовлетворительной оценки\",\"При перерыве в работе в данной должности более 6 месяцев\",\"В любом из перечисленных случаев\"]', '', 5, ''),
(78, 186952, 'Что является определением термина \"защитное заземление\"?', '[\"Заземление, выполняемое в целях электробезопасности\",\"Заземление точки или точек токоведущих частей электроустановки, выполняемое для обеспечения работы электроустановки\",\"Преднамеренное электрическое соединение какой-либо точки сети, электроустановки или оборудования с заземляющим устройством\"]', '', 1, ''),
(79, 186953, 'Как учитывается время, затраченное на проведение противоаварийных и противопожарных тренировок в соответствии с требованиями \"Правил работы с персоналом в организациях электроэнергетики Российской Федерации\"?', '[\"Включается в рабочее время тренирующихся\",\"Учитывается и оплачивается как работа в выходной или не рабочий праздничный день\"]', '', 1, ''),
(80, 186955, 'Кем выполняется подготовка рабочего места для выполнения строительно-монтажных работ?', '[\"Работниками организации - владельца электроустановки\",\"Работниками строительно-монтажной организации\",\"Работниками строительно-монтажной организации и организации - владельца электроустановок\"]', '', 1, ''),
(81, 186956, 'Кто должен выполнять уборку помещений распределительных устройств и очистку электрооборудования?', '[\"Обученный персонал с соблюдением правил безопасности\",\"Оперативно-ремонтный персонал, обслуживающий данную установку\",\"Ремонтный персонал с группой по электробезопасности не ниже IV\"]', '', 1, ''),
(82, 186957, 'Что может быть применено для защиты при косвенном прикосновении в цепях, питающих переносные электроприемники?', '[\"Автоматическое отключение питания\",\"Защитное электрическое разделение цепей\",\"Сверхнизкое напряжение\",\"Двойная изоляция\",\"Любая из перечисленных мер защиты, в зависимости от категории помещения по уровню опасности поражения людей электрическим током\"]', '', 5, ''),
(83, 186958, 'Допускается ли совмещение контрольных противоаварийных тренировок и контрольных противопожарных тренировок в соответствии с требованиями \"Правил работы с персоналом в организациях электроэнергетики Российской Федерации\"?', '[\"Да\",\"Нет\",\"По согласованию с МЧС\"]', '', 1, '');
