INSERT INTO `l4` (`id`, `globalId`, `question`, `answers`, `pravila`, `correctAnswer`, `glava`) VALUES
(1, 185985, 'На кого распространяются Правила по охране труда при эксплуатации электроустановок?', '[\"На работников промышленных предприятий, в составе которых имеются электроустановки\",\"На работников организаций независимо от форм собственности и организационно-правовых форм и других физических лиц, занятых техническим обслуживанием электроустановок, проводящих в них оперативные переключения, организующих и выполняющих испытания и измерения\",\"На работодателей - юридических и физических лиц независимо от их организационно-правовых форм и работников из числа электротехнического, электротехнологического и неэлектротехнического персонала\",\"На работников всех организаций независимо от формы собственности, занятых техническим обслуживанием электроустановок и выполняющих в них строительные, монтажные и ремонтные работы\"]', '', 3, ''),
(2, 185986, 'На какой срок может быть продлен наряд-допуск на производство работ в электроустановках?', '[\"1 раз на срок не более 20 календарных дней со дня продления\",\"1 раз на срок не более 30 календарных дней со дня продления\",\"1 раз на срок не более 15 календарных дней со дня продления\"]', '', 3, ''),
(3, 185987, 'Каким образом производится присвоение группы I персоналу, усвоившему требования по электробезопасности?', '[\"Путем проведения инструктажа с последующим проведением экзамена с использованием компьютерной техники\",\"Присвоение группы I по электробезопасности производится путем проведения инструктажа, который, как правило, должен завершаться проверкой знаний в форме устного опроса и (при необходимости) проверкой приобретенных навыков безопасных способов работы или оказания первой помощи при поражении электрическим током\",\"Путем проведения инструктажа, а затем - прохождением стажировки не менее 5 рабочих смен с последующей проверкой приобретенных навыков безопасных способов работы\"]', '', 2, ''),
(4, 185988, 'За что несут персональную ответственность работники, непосредственно обслуживающие электроустановки?', '[\"За несвоевременное и неудовлетворительное техническое обслуживание электроустановок\",\"За нарушения, происшедшие по их вине, а также за неправильную ликвидацию ими нарушений в работе электроустановок на обслуживаемом участке\",\"За нарушения в работе, вызванные низким качеством ремонта\",\"За нарушения в эксплуатации электротехнологического оборудования\"]', '', 2, ''),
(5, 185989, 'Что из перечисленного не входит в комплект документации, хранящейся на рабочем месте оперативного персонала?', '[\"Журнал учета работ по нарядам и распоряжениям\",\"Журнал релейной защиты, автоматики и телемеханики\",\"Списки работников, имеющих право выполнения оперативных переключений, ведения оперативных переговоров и единоличного осмотра электроустановок\",\"Журнал регистрации вводного инструктажа\",\"Перечень работ, выполняемых в порядке текущей эксплуатации\"]', '', 4, ''),
(6, 185990, 'Можно ли использовать землю в качестве фазного или нулевого провода в электроустановках до 1000 В?', '[\"Разрешается без ограничений\",\"Правилами технической эксплуатации электроустановок потребителей запрещается\",\"Разрешается только в единичных случаях с разрешения органов энергонадзора\"]', '', 2, ''),
(7, 185991, 'Что является определением термина \"защита при косвенном прикосновении\"?', '[\"Защита от поражения электрическим током при прикосновении к открытым проводящим частям, оказавшимся под напряжением, при повреждении изоляции\",\"Защита от напряжения, возникающего при стекании тока с заземлителя в землю, между точкой ввода тока в заземлитель и зоной нулевого потенциала\",\"Защита для предотвращения прикосновения к токоведущим частям, находящимся под напряжением\"]', '', 1, ''),
(8, 185992, 'Что, согласно Правилам устройства электроустановок, называется распределительной сетью?', '[\"Сеть от распределительного устройства подстанции или ответвления от воздушных линий электропередачи до ВУ, ВРУ, ГРЩ\",\"Сеть от ВУ, ВРУ, ГРЩ до распределительных пунктов, щитков и пунктов питания наружного освещения\",\"Сеть от щитков до светильников, штепсельных розеток и других электроприемников\",\"Система, осуществляющая последовательное включение (отключение) участков групповой сети наружного освещения\"]', '', 2, ''),
(9, 185993, 'Каким образом работник при непосредственном использовании может определить, что электрозащитные средства прошли эксплуатационные испытания и пригодны для применения?', '[\"По протоколам эксплуатационных испытаний\",\"По штампу или маркировке на средстве защиты\",\"По записи в Журнале испытаний средств защиты\",\"По внешнему виду средств защиты\"]', '', 2, ''),
(10, 185994, 'Какой персонал не проходит подготовку по новой должности (рабочему месту)?', '[\"Диспетчерский персонал\",\"Оперативный персонал\",\"Оперативно-ремонтный персонал\",\"Ремонтный персонал\",\"Административно-технический персонал\"]', '', 5, ''),
(11, 185995, 'Каким образом оформляются результаты проверки знаний по охране труда Потребителей?', '[\"На основании протокола проверки знаний правил работы в электроустановках - в журнале установленной формы\",\"На основании указаний председателя комиссии по проверке знаний\",\"Фиксируются в \\\"Журнале учета проверки знаний правил работы в электроустановках\\\" рекомендуемого образца\",\"Правилами не регламентировано\"]', '', 3, ''),
(12, 185996, 'Кто имеет право на продление наряд-допуска?', '[\"Только работник, выдавший наряд-допуск, или имеющий право выдачи наряда-допуска в данной электроустановке\",\"Ответственный руководитель работ в данной электроустановке\",\"Ответственный за электрохозяйство структурного подразделения\",\"Руководитель объекта, на котором проводятся работы\"]', '', 1, ''),
(13, 185997, 'Кем проводится присвоение I группы по электробезопасности?', '[\"Присвоение I группы по электробезопасности проводится работником из числа электротехнического персонала, имеющего группу III по электробезопасности, назначенным распоряжением руководителя организации\",\"Присвоение I группы по электробезопасности проводится работником из числа электротехнического персонала, имеющего группу IV по электробезопасности, назначенным распоряжением руководителя организации\",\"Присвоение I группы по электробезопасности проводится работником из числа электротехнического персонала, прошедшего проверку знаний в комиссии территориального органа Ростехнадзора\"]', '', 1, ''),
(14, 185998, 'За что несут персональную ответственность работники, осуществляющие ремонтные работы в электроустановках?', '[\"За несвоевременное и неудовлетворительное техническое обслуживание электроустановок\",\"За нарушения, происшедшие по их вине, а также за неправильную ликвидацию ими нарушений в работе электроустановок на обслуживаемом участке\",\"За нарушения в эксплуатации электротехнологического оборудования\",\"За нарушения в работе, вызванные низким качеством ремонта\"]', '', 4, ''),
(15, 185999, 'Как часто должны пересматриваться производственные инструкции по эксплуатации электроустановок?', '[\"Периодически, один раз в год\",\"В случае изменений условий эксплуатации, но не реже одного раза в три года\",\"Периодически, но не реже одного раза в пять лет\"]', '', 2, ''),
(16, 186000, 'Когда проводится проверка состояния защиты от перенапряжений распределительных устройств?', '[\"Ежегодно, в любое время\",\"Ежегодно, перед началом грозового сезона\",\"Ежегодно перед началом и по окончании грозового сезона\",\"Два раза в год - весной и осенью\"]', '', 2, ''),
(17, 186001, 'Что является определением термина \"заземлитель\"?', '[\"Проводящая часть, не являющаяся частью электроустановки\",\"Проводящая часть или совокупность соединенных между собой проводящих частей, находящихся в электрическом контакте с землей непосредственно или через промежуточную проводящую среду\",\"Сторонняя проводящая часть, находящаяся в электрическом контакте с землей непосредственно или через промежуточную проводящую среду, используемая для целей заземления\"]', '', 2, ''),
(18, 186002, 'Что, согласно Правилам устройства электроустановок, называется групповой сетью?', '[\"Сеть от распределительного устройства подстанции или ответвления от воздушных линий электропередачи до ВУ, ВРУ, ГРЩ\",\"Сеть от ВУ, ВРУ, ГРЩ до распределительных пунктов, щитков и пунктов питания наружного освещения\",\"Сеть от щитков до светильников, штепсельных розеток и других электроприемников\",\"Система, осуществляющая последовательное включение (отключение) участков групповой сети наружного освещения\"]', '', 3, ''),
(19, 186003, 'Как должны маркироваться средства защиты, не выдержавшие испытания?', '[\"Штамп испытания должен быть перечеркнут красной краской\",\"Штамп испытания должен быть перечеркнут черной краской\",\"Штамп испытания должен быть перечеркнут белой краской\",\"Средство защиты должно быть механически повреждено\"]', '', 1, ''),
(20, 186004, 'В каком объеме должна проводиться стажировка для диспетчерского, оперативного, оперативно-ремонтного и ремонтного персонала?', '[\"В объеме, определенном программой подготовки по новой должности\",\"В объеме, 72 академических часов\",\"В объеме, 160 академических часов\",\"В объеме, 240 академических часов\"]', '', 1, ''),
(21, 186005, 'Право проведения каких работ должно быть зафиксировано в удостоверении о проверке знаний правил работы в электроустановках в графе \"Свидетельство на право проведения специальных работ\"?', '[\"Отсоединение и присоединение кабеля, проводов электродвигателя и отдельных электроприемников инженерного оборудования зданий и сооружений\",\"Ремонт пусковой и коммутационной аппаратуры\",\"Ремонт отдельно расположенных магнитных станций и блоков управления, уход за щеточным аппаратом электрических машин и смазка подшипников\",\"Работы, выполняемые со снятием рабочего напряжения с электроустановки или ее части с прикосновением к токоведущим частям, находящимся под наведенным напряжением более 25 В на рабочем месте или на расстоянии от этих токоведущих частей менее допустимого\"]', '', 4, ''),
(22, 186006, 'Каким способом может быть передано разрешение на продление наряд-допуска?', '[\"Только по телефону дежурному диспетчеру с записью в оперативном журнале\",\"Только с нарочным допускающему с последующей записью в строке наряда \\\"Отдельные указания\\\"\",\"Только по радио производителю работ с последующей подписью в таблице наряда-допуска \\\"Разрешение на подготовку рабочих мест и на допуск к выполнению работ\\\"\",\"По телефону, радио или с нарочным допускающему, ответственному руководителю работ, производителю работ или наблюдающему (в случае если ответственный руководитель работ и производитель работ не назначаются). В этом случае допускающий, ответственный руководитель работ, производитель работ или наблюдающий за своей подписью указывает в наряд-допуске фамилию и инициалы работника, продлившего наряд-допуск-допуск\"]', '', 4, ''),
(23, 186007, 'Какие существуют возрастные ограничения для присвоения III группы по электробезопасности?', '[\"Никаких ограничений по возрасту нет\",\"Группа III может присваиваться работникам только по достижении 16-летнего возраста\",\"Группа III может присваиваться работникам только по достижении 18-летнего возраста\",\"Группа III может присваиваться работникам только по достижении 21-летнего возраста\"]', '', 3, ''),
(24, 186008, 'Какая ответственность предусмотрена за нарушение Правил технической эксплуатации электроустановок потребителей?', '[\"Уголовная\",\"Административная\",\"Дисциплинарная\",\"В соответствии с действующим законодательством Российской Федерации\"]', '', 4, ''),
(25, 186009, 'Что необходимо предпринять при образовании на гравийной засыпке маслоприемников трансформаторов твердых отложений от нефтепродуктов толщиной более 3 мм?', '[\"Заменить гравий\",\"Застелить гравий досками\",\"Засыпать гравий слоем песка\"]', '', 1, ''),
(26, 186010, 'Какая вода должна применяться для доливки аккумуляторов?', '[\"Водопроводная\",\"Котловая\",\"Дистиллированная\",\"Дождевая\"]', '', 3, ''),
(27, 186011, 'Что является определением термина \"искусственный заземлитель\"?', '[\"Заземлитель, специально выполняемый для целей заземления\",\"Преднамеренное электрическое соединение какой-либо точки сети, электроустановки или оборудования с заземляющим устройством\",\"Сторонняя проводящая часть, находящаяся в электрическом контакте с землей непосредственно или через промежуточную проводящую среду, используемая для целей заземления\"]', '', 1, ''),
(28, 186012, 'Какие условия для обычного исполнения светильников, согласно Правилам устройства электроустановок, должны соблюдаться при применении люминесцентных ламп в осветительных установках?', '[\"Температура окружающей среды не должна быть выше 30 °С; напряжение у осветительных приборов должно быть не более 90 % номинального\",\"Температура окружающей среды не должна быть выше 35 °С; напряжение у осветительных приборов должно быть не более 95 % номинального\",\"Температура окружающей среды не должна быть ниже 5 °С; напряжение у осветительных приборов должно быть не менее 90% номинального\",\"Температура окружающей среды не должна быть ниже 0 °С; напряжение у осветительных приборов должно быть не менее 80% номинального\"]', '', 3, ''),
(29, 186013, 'Какое значение напряжения должно применяться для испытания основных изолирующих электрозащитных средств, предназначенных для электроустановок напряжением выше 1 до 35 кВ включительно?', '[\"Равное 3-кратному линейному, но не ниже 40 кВ\",\"Равное 3-кратному линейному, но не выше 40 кВ\",\"Равное 3-кратному фазному\",\"Равное 2-кратному фазному\"]', '', 1, ''),
(30, 186014, 'Кто проводит стажировку для диспетчерского, оперативного, оперативно-ремонтного и ремонтного персонала?', '[\"Проводится под руководством работника, ответственного за стажировку, назначенного организационно-распорядительным документом\",\"Руководителем организации\",\"Представителем Ростехнадзора\"]', '', 1, ''),
(31, 186015, 'Какую группу по электробезопасности должны иметь работники из числа оперативного персонала, единолично обслуживающие электроустановки напряжением до 1000 В?', '[\"Группу III\",\"Группу V\",\"Группу II или III\"]', '', 1, ''),
(32, 186016, 'После какого срока могут быть уничтожены наряд-допуски, работы по которым полностью закончены и не имели место аварии, инциденты и несчастные случаи?', '[\"По истечении 30 суток\",\"По истечении 1 года\",\"По истечении 3 месяцев\",\"По истечении 6 месяцев\"]', '', 2, ''),
(33, 186017, 'Какой минимальный стаж работы в электроустановках должен быть у работника с высшим профессиональным (техническим) образованием в области электроэнергетики для перехода с третьей группы электробезопасности на четвертую?', '[\"3 месяца в предыдущей группе\",\"2 месяца в предыдущей группе\",\"6 месяцев в предыдущей группе\",\"1 месяц в предыдущей группе\"]', '', 2, ''),
(34, 186018, 'В течение какого срока проводится комплексное опробование основного и вспомогательного оборудования электроустановки перед приемкой в эксплуатацию?', '[\"В течение 24 часов\",\"В течение 48 часов\",\"В течение 72 часов\",\"В течение 120 часов\"]', '', 3, ''),
(35, 186019, 'С какой периодичностью в распределительных электрических сетях напряжением до 20 кВ включительно производят измерения нагрузок и напряжений трансформаторов?', '[\"В первый год эксплуатации не менее 2 раз - в период максимальных и минимальных нагрузок, в дальнейшем - по необходимости\",\"В первый год эксплуатации - в период максимальных нагрузок, в дальнейшем - по необходимости\",\"В первый год эксплуатации - в период максимальных нагрузок, во второй год - в период минимальных нагрузок, в дальнейшем - по необходимости\"]', '', 1, ''),
(36, 186020, 'Как часто должна проводиться периодическая проверка переносных и передвижных электроприемников?', '[\"Не реже 1 раза в месяц\",\"Не реже 1 раза в 3 месяца\",\"Не реже 1 раза в 6 месяцев\"]', '', 3, ''),
(37, 186021, 'Что является определением термина \"естественный заземлитель\"?', '[\"Проводящая часть или совокупность соединенных между собой проводящих частей, находящихся в электрическом контакте с землей непосредственно или через промежуточную проводящую среду\",\"Проводящая часть, не являющаяся частью электроустановки\",\"Сторонняя проводящая часть, находящаяся в электрическом контакте с землей непосредственно или через промежуточную проводящую среду, используемая для целей заземления\"]', '', 3, ''),
(38, 186022, 'Светильники какого класса защиты, согласно Правилам устройства электроустановок, необходимо применять (за исключением светильников, обслуживаемых с кранов) в помещениях с повышенной опасностью и особо опасных помещениях при высоте установки светильников общего освещения над полом или площадкой обслуживания менее 2,5 м при условии, что цепь не защищена устройством защитного отключения?', '[\"Класса защиты 0 или 1\",\"Класса защиты 2 или 3\",\"Только класса защиты 0\",\"Только класса защиты 3\"]', '', 2, ''),
(39, 186023, 'Какая должна быть, как правило, длительность приложения полного испытательного напряжения для изолирующих средств защиты из слоистых диэлектриков?', '[\"1 минута\",\"2 минуты\",\"3 минуты\",\"5 минут\"]', '', 4, ''),
(40, 186024, 'Какие рабочие места предусмотрены во время стажировки для оперативного, оперативно-ремонтного персонала?', '[\"Свое рабочее место и (или) рабочие места оперативного персонала объектов электроэнергетики такой организации, определенные программой подготовки по новой должности\",\"Рабочее место в своем диспетчерском центре, а также рабочее место диспетчера в полнофункциональном резервном диспетчерском центре (при его наличии)\",\"Свое рабочее место и (или) специально оборудованные полигоны и мастерские\"]', '', 1, ''),
(41, 186025, 'При каком условии работники, не обслуживающие электроустановки, могут допускаться в РУ до 1000 В?', '[\"В сопровождении оперативного персонала, обслуживающего данную электроустановку, имеющего группу IV, либо работника, имеющего право единоличного осмотра\",\"В сопровождении оперативного персонала, обслуживающего данную электроустановку, имеющего группу III, либо работника, имеющего право единоличного осмотра\",\"В сопровождении опытного работника из числа ремонтного персонала, имеющего группу по электробезопасности не ниже V\"]', '', 2, ''),
(42, 186026, 'Каким образом в электроустановках ведется учет производства работ по нарядам-допускам и распоряжениям?', '[\"В журнале проведения целевого инструктажа\",\"В журнале произвольной формы\",\"В папке действующих нарядов\",\"В журнале учета работ по нарядам-допускам и распоряжениям\"]', '', 4, ''),
(43, 186027, 'В каком случае удостоверение о проверке знаний правил работы в электроустановках подлежит замене?', '[\"По истечении срока действия группы по электробезопасности\",\"В случае утери удостоверения\",\"При повышении группы по электробезопасности\",\"В случае изменения должности\"]', '', 4, ''),
(44, 186028, 'Кто проводит комплексное опробование оборудования перед приемкой в эксплуатацию электроустановок?', '[\"Организация, осуществляющая строительство и монтаж энергообъекта\",\"Организация-заказчик\",\"Организация-подрядчик с привлечением персонала заказчика\"]', '', 2, ''),
(45, 186029, 'Кто обязан организовать обучение, проверку знаний, инструктаж персонала в соответствии с требованиями государственных стандартов, настоящих Правил, правил безопасности труда и местных инструкций?', '[\"Руководитель Потребителя\",\"Технический руководитель Потребителя\",\"Ответственный за электрохозяйство Потребителя\",\"Руководители подразделений Потребителя\"]', '', 1, ''),
(46, 186030, 'Кто проводит ремонт переносных электроприемников?', '[\"Оперативно-ремонтный персонал Потребителя\",\"Ремонтный персонал Потребителя\",\"Специализированная организация (подразделение)\",\"Электротехнический персонал Потребителя, имеющий III группу по электробезопасности\"]', '', 3, ''),
(47, 186031, 'Что является определением термина \"заземление\"?', '[\"Сторонняя проводящая часть, находящаяся в электрическом контакте с землей непосредственно или через промежуточную проводящую среду, используемая для целей заземления\",\"Заземление точек токоведущих частей электроустановки, выполняемое для обеспечения работы электроустановки\",\"Преднамеренное электрическое соединение какой-либо точки сети, электроустановки или оборудования с заземляющим устройством\"]', '', 3, ''),
(48, 186032, 'Светильники какого минимального класса защиты, согласно Правилам устройства электроустановок, допускается применять (за исключением светильников, обслуживаемых с кранов) в помещениях с повышенной опасностью и особо опасных помещениях при высоте установки светильников общего освещения над полом или площадкой обслуживания менее 2,5 м при условии, что цепь защищена устройством защитного отключения (УЗО) с током срабатывания до 30 мА?', '[\"Класса защиты 0\",\"Класса защиты 1\",\"Класса защиты 2\",\"Класса защиты 3\"]', '', 2, ''),
(49, 186033, 'Какое обучение в обязательном порядке должны пройти сотрудники, чтобы получить допуск к работе на объекте?', '[\"Обучение на курсах повышения квалификации по специальности\",\"Обучение правилам действия в чрезвычайных ситуациях\",\"Обучение санитарно-гигиеническим правилам\",\"Обучение мерам пожарной безопасности\"]', '', 4, ''),
(50, 186034, 'Какие рабочие места предусмотрены во время стажировки для ремонтного персонала?', '[\"Свое рабочее место и (или) рабочие места оперативного персонала объектов электроэнергетики такой организации, определенные программой подготовки по новой должности\",\"Рабочее место в своем диспетчерском центре, а также рабочее место диспетчера в полнофункциональном резервном диспетчерском центре (при его наличии)\",\"Свое рабочее место и (или) специально оборудованные полигоны и мастерские\"]', '', 3, ''),
(51, 186035, 'В каких целях допускается приближение на расстояние менее 8 метров к месту возникновения короткого замыкания на землю при работах на воздушной линии электропередачи?', '[\"Только для оказания доврачебной помощи людям, попавшим под напряжение\",\"Только для определения визуального расстояния до опоры воздушной линии\",\"Только для оперативных переключений с целью ликвидации замыкания и освобождения людей, попавших под напряжение\"]', '', 3, ''),
(52, 186036, 'Какие требования установлены Правилами по охране труда при эксплуатации электроустановок по ведению журнала учета работ по нарядам-допускам и распоряжениям?', '[\"Форму журнала определяет руководитель структурного подразделения в зависимости от специфики деятельности\",\"Независимо от принятого в организации порядка учета работ по нарядам-допускам и распоряжениям факт допуска к работе должен быть зарегистрирован записью в оперативном документе\",\"Ведение журнала учета работ по нарядам и распоряжениям не допускается в электронной форме с применением автоматизированных систем и использованием электронной подписи\"]', '', 2, ''),
(53, 186037, 'Что является подтверждением проведения и получения целевого инструктажа членами бригады?', '[\"Подписи членов бригады в таблицах регистрации целевых инструктажей\",\"Подписи ответственного руководителя работ в таблицах регистрации целевых инструктажей\",\"Запись в таблице регистрации целевого инструктажа\"]', '', 1, ''),
(54, 186039, 'Где проводится проверка знаний работников Потребителя, если их численность не позволяет создать собственную комиссию?', '[\"В комиссии родственной организации\",\"В территориальной комиссии Ростехнадзора\",\"В образовательных учреждениях без привлечения инспектора Ростехнадзора\",\"Ответственному за электрохозяйство разрешается единолично проводить проверку знаний\"]', '', 2, ''),
(55, 186040, 'Какое напряжение должно применяться для питания переносных (ручных) светильников, применяемых в помещениях с повышенной опасностью?', '[\"Не выше 12 В\",\"Не выше 42 В\",\"Не выше 50 В\",\"Не выше 127 В\"]', '', 3, ''),
(56, 186041, 'Что является определением термина \"защитное заземление\"?', '[\"Заземление, выполняемое в целях электробезопасности\",\"Заземление точки или точек токоведущих частей электроустановки, выполняемое для обеспечения работы электроустановки\",\"Преднамеренное электрическое соединение какой-либо точки сети, электроустановки или оборудования с заземляющим устройством\"]', '', 1, ''),
(57, 186042, 'Какое напряжение, согласно Правилам устройства электроустановок, должно применяться для питания переносных светильников в помещениях с повышенной опасностью и особо опасных помещениях?', '[\"Напряжение не выше 50 В\",\"Напряжение не выше 110 В\",\"Напряжение не выше 220 В\",\"Напряжение не выше 127 В\"]', '', 1, ''),
(58, 186043, 'При каком количестве людей, единовременно находящихся на этаже здания сооружения, должны быть вывешены на видных местах планы эвакуации людей?', '[\"10 и более человек\",\"8 и более человек\",\"5 и более человек\"]', '', 1, ''),
(59, 186044, 'Какие требования в процессе стажировки оперативный, оперативно-ремонтный и ремонтный персонал должен усвоить?', '[\"Только требования отраслевых актов\",\"Только требования инструктивно-технических документов в сфере электроэнергетики\",\"Требования отраслевых актов и инструктивно-технических документов в сфере электроэнергетики и теплоснабжения\"]', '', 3, ''),
(60, 186045, 'Кто дает разрешение на снятие напряжения при несчастных случаях для освобождения пострадавшего от действия электрического тока?', '[\"Разрешение дает оперативный персонал энергообъекта\",\"Разрешение дает вышестоящий оперативный персонал\",\"Разрешение дает административно-технический персонал\",\"Предварительного разрешения оперативного персонала не требуется. Напряжение должно быть снято немедленно\"]', '', 4, ''),
(61, 186046, 'На какое количество присоединений допускается выдавать наряд-допуск в электроустановках выше 1000 В, где напряжение снято со всех токоведущих частей, в том числе с вводов воздушной линии электропередачи и кабельной линии, и заперт вход в соседние электроустановки?', '[\"На каждое присоединение - один наряд-допуск\",\"На каждую секцию - один наряд-допуск для работы на части присоединений\",\"Один наряд-допуск для одновременной работы на сборных шинах и всех присоединениях\"]', '', 3, ''),
(62, 186047, 'Какие работники могут выполнять единоличный осмотр электроустановок, электротехнической части технологического оборудования напряжением до 1000 В?', '[\"Работник из числа ремонтного персонала, имеющий группу не ниже III\",\"Работник из числа административно-технического персонала, имеющий группу IV\",\"Работник, имеющий группу III и право единоличного осмотра на основании письменного распоряжения руководителя организации\",\"Работник из числа оперативного персонала, имеющий группу по электробезопасности не ниже III, осуществляющий оперативное обслуживание данной электроустановки, находящийся на дежурстве, либо работник из числа административно-технического персонала, на которого возложены обязанности по организации технического и оперативного обслуживания, проведения ремонтных, монтажных и наладочных работ в электроустановках (далее - административно-технический персонал), имеющий группу IV по электробезопасности\"]', '', 4, ''),
(63, 186048, 'В течение какого срока проводится комплексное опробование работы линии электропередачи перед приемкой в эксплуатацию?', '[\"В течение 24 часов\",\"В течение 48 часов\",\"В течение 72 часов\",\"В течение 36 часов\"]', '', 1, ''),
(64, 186049, 'Каким образом оформляются результаты проверки знаний персонала по электробезопасности?', '[\"Результаты проверки заносятся в журнал установленной формы, персоналу, успешно прошедшему проверку знаний, выдается удостоверение установленной формы\",\"Результаты проверки заносятся в журнал установленной формы\",\"Результаты проверки оформляются протоколом установленной формы, персоналу, успешно прошедшему проверку знаний, выдается удостоверение установленной формы\",\"Результаты проверки заносятся в трудовую книжку и в удостоверение установленной формы, которое выдается персоналу, успешно прошедшему проверку знаний\"]', '', 1, ''),
(65, 186050, 'Как классифицируются помещения в отношении опасности поражения людей электрическим током?', '[\"Помещения без повышенной опасности и помещения с повышенной опасностью\",\"Помещения без повышенной опасности, помещения с повышенной опасностью, особо опасные помещения\",\"Неопасные, опасные и особо опасные помещения\",\"Неопасные, малоопасные, опасные и особо опасные помещения\"]', '', 2, ''),
(66, 186051, 'Что является определением термина \"основная изоляция\"?', '[\"Изоляция в электроустановках напряжением до 1 кВ, обеспечивающая степень защиты от поражения электрическим током\",\"Изоляция токоведущих частей, обеспечивающая, в том числе, защиту от прямого прикосновения\",\"Независимая изоляция в электроустановках напряжением до 1 кВ, для защиты при косвенном прикосновении\"]', '', 2, ''),
(67, 186052, 'На какие виды, согласно Правилам устройства электроустановок, делится аварийное освещение?', '[\"Дежурное освещение и эвакуационное освещение\",\"Общее освещение и сигнальное освещение\",\"Освещение безопасности и эвакуационное освещение\",\"Рабочее освещение и комбинированное освещение\"]', '', 3, ''),
(68, 186053, 'В каком случае, в дополнение к плану эвакуации, должна быть разработана инструкция, определяющая действие персонала по эвакуации людей?', '[\"На объектах с массовым пребыванием людей\",\"На объектах с постоянным пребыванием людей в количестве 40 человек\",\"На объектах с постоянным пребыванием людей в количестве 30 человек\",\"На объектах с постоянным пребыванием людей в количестве 20 и более человек\"]', '', 1, ''),
(69, 186054, 'Какой минимальный срок стажировки на каждом рабочем месте?', '[\"7 рабочих дней (смен)\",\"5 рабочих дней (смен)\",\"2 рабочих дня (смены)\",\"10 рабочих дней (смен)\"]', '', 3, ''),
(70, 186055, 'В каком из перечисленных случаев допускается заменять предохранители под напряжением и под нагрузкой?', '[\"Только при снятии и установке предохранителей во вторичных цепях\",\"Только при снятии и установке предохранителей пробочного типа\",\"Только при снятии и установке предохранителей трансформаторов напряжения\",\"В любом из перечисленных случаев\"]', '', 4, ''),
(71, 186056, 'Когда допускается выдавать один наряд-допуск для одновременного или поочередного выполнения работ на разных рабочих местах одной электроустановки?', '[\"Для работы на электродвигателях одного напряжения и присоединениях одного РУ\",\"Для выполнения ремонта и обслуживания устройств проводной радио- и телефонной связи\",\"Для работы по обслуживанию сети наружного освещения\"]', '', 1, ''),
(72, 186057, 'У кого могут быть на учете ключи от электроустановок, не имеющих местного оперативного персонала? Выберите два правильных варианта ответа.\r\nА) У административно-технического персонала\r\nБ) У руководящих работников и специалистов организации\r\nВ) У специалистов по охране труда организации', '[\"1) A и Б\",\"2) А и В\",\"3) Б и В\"]', '', 1, ''),
(73, 186058, 'Можно ли принимать в эксплуатацию электроустановки с дефектами и недоделками?', '[\"Можно, с условием устранения недоделок в течение месяца со дня приемки электроустановки в эксплуатацию\",\"Можно, если на это есть разрешение энергонадзора\",\"Можно, если имеющиеся дефекты не влияют на работу электроустановки\",\"Приемка в эксплуатацию электроустановок с недоделками не допускается\"]', '', 4, ''),
(74, 186059, 'Чем должны отличаться светильники аварийного освещения от светильников рабочего освещения?', '[\"Исполнением\",\"Знаками или окраской\",\"Принципиальных отличий нет\"]', '', 2, ''),
(75, 186060, 'Какие помещения относятся к помещениям с повышенной опасностью поражения людей электрическим током?', '[\"Помещения, характеризующиеся наличием сырости или токопроводящей пыли\",\"Помещения, характеризующиеся наличием металлических, земляных, железобетонных и других токопроводящих полов\",\"Помещения, характеризующиеся наличием высокой температуры\",\"Помещения, характеризующиеся возможностью одновременного прикосновения человека к металлоконструкциям зданий, имеющим соединение с землей, технологическим аппаратам, механизмам и т. п. с одной стороны, и к металлическим корпусам электрооборудования (открытым проводящим частям) - с другой\",\"Любое из перечисленных помещений относится к помещениям с повышенной опасностью\"]', '', 5, ''),
(76, 186061, 'Что является определением термина \"двойная изоляция\"?', '[\"Изоляция в электроустановках напряжением до 1 кВ, состоящая из основной и дополнительной изоляции\",\"Независимая изоляция в электроустановках напряжением до 1 кВ, выполняемая дополнительно к основной изоляции для защиты при косвенном прикосновении\",\"Изоляция в электроустановках напряжением до 1 кВ, обеспечивающая степень защиты от поражения электрическим током, равноценную двойной изоляции\"]', '', 1, ''),
(77, 186062, 'Для чего, согласно Правилам устройства электроустановок, предназначено освещение безопасности?', '[\"Для продолжения работы при аварийном отключении рабочего освещения\",\"Для освещения территории в нерабочее время\",\"Для установки вдоль границ территорий, охраняемых специальным персоналом\",\"Для обеспечения освещения вне производственных помещений\"]', '', 1, ''),
(78, 186063, 'С какой периодичностью на объектах с массовым пребыванием людей должны проводиться практические тренировки по эвакуации людей при пожаре?', '[\"Не реже 1 раза в полугодие\",\"Не реже 1 раза в 9 месяцев\",\"Не реже 1 раза в год\",\"Не реже 1 раза в 3 месяца\"]', '', 1, ''),
(79, 186064, 'Какой максимальный срок стажировки на каждом рабочем месте?', '[\"7 рабочих дней (смен)\",\"5 рабочих дней (смен)\",\"14 рабочих дней (смен)\",\"10 рабочих дней (смен)\"]', '', 3, ''),
(80, 186065, 'В каком случае нарушен порядок хранения и выдачи ключей?', '[\"Ключи от электроустановок должны быть пронумерованы и храниться в запираемом ящике. Один комплект должен быть запасным\",\"Выдача ключей должна быть заверена подписью работника, ответственного за выдачу и хранение ключей, а также подписью работника, получившего ключи\",\"Ключи от электроустановок должны выдаваться производителю работ при допуске к работам по наряду-допуску от помещений, вводных устройств, щитов, щитков, в которых предстоит работать\",\"Допускается возвращать ключи от электроустановок оперативному персоналу в течение трех дней после полного окончания работ\"]', '', 4, ''),
(81, 186066, 'В каком из перечисленных случаев допускается выдавать один наряд-допуск для одновременного или поочередного выполнения работ на разных рабочих местах одной электроустановки?', '[\"Только при прокладке и перекладке силовых и контрольных кабелей, испытаниях электрооборудования, проверке устройств защиты, измерений, блокировки, электроавтоматики, телемеханики, связи\",\"Только при ремонте отдельного кабеля в туннеле, коллекторе, колодце, траншее, котловане\",\"Только при ремонте коммутационных аппаратов одного присоединения, в том числе когда их приводы находятся в другом помещении\",\"Во всех перечисленных случаях\"]', '', 4, ''),
(82, 186067, 'Как должны выполняться работы по расчистке трассы воздушной линии электропередачи от деревьев согласно Правилам по охране труда при эксплуатации электроустановок?', '[\"Только по распоряжению\",\"Только по плану производства работ\",\"Только по наряду-допуску\",\"По наряду-допуску или распоряжению\"]', '', 4, ''),
(83, 186068, 'На какие категории подразделяется электротехнический персонал организации?', '[\"На административно-технический, оперативный и ремонтный\",\"На оперативный, ремонтный и оперативно-ремонтный\",\"На административно-технический, оперативно-ремонтный, оперативный и ремонтный\",\"На административный, ремонтный и оперативный\"]', '', 3, ''),
(84, 186069, 'Какая проверка знаний проводится у персонала при назначении или переводе на другую работу, если новые обязанности требуют дополнительных знаний норм и правил?', '[\"Первичная\",\"Повторная\",\"Очередная\",\"Внеочередная\"]', '', 4, ''),
(85, 186070, 'Какие помещения, согласно ПУЭ, называются сырыми?', '[\"Помещения, в которых относительная влажность воздуха не превышает 60 %\",\"Помещения, в которых относительная влажность воздуха превышает 75 %\",\"Помещения, в которых относительная влажность воздуха не превышает 90 %\",\"Помещения, в которых относительная влажность воздуха близка к 100 %\"]', '', 2, ''),
(86, 186071, 'Что является определением термина \"усиленная изоляция\"?', '[\"Независимая изоляция в электроустановках напряжением до 1 кВ, выполняемая дополнительно к основной изоляции для защиты при косвенном прикосновении\",\"Изоляция в электроустановках напряжением до 1 кВ, обеспечивающая степень защиты от поражения электрическим током, равноценная двойной изоляции\",\"Изоляция в электроустановках напряжением до 1 кВ, состоящая из основной и дополнительной изоляции\"]', '', 2, ''),
(87, 186072, 'Что, согласно Правилам устройства электроустановок, может применяться для питания групп светильников вместо групповых щитков при использовании шинопроводов в качестве линий питающей осветительной сети?', '[\"Индивидуальные модульные учетно-распределительные щитки\",\"Только присоединяемые к шинопроводу отдельные аппараты защиты\",\"Только присоединяемые к шинопроводу отдельные аппараты управления\",\"Присоединяемые к шинопроводу отдельные аппараты защиты и управления\"]', '', 4, ''),
(88, 186073, 'Куда должны складываться использованные промасленные обтирочные материалы?', '[\"В контейнеры из негорючего материала с закрывающейся крышкой\",\"В специальные контейнеры вместимостью не более 1,0 м3 с надписью \\\"Для ветоши\\\"\",\"В деревянные закрывающиеся ящики, обработанные огнезащитными составами, вместимостью не более 1,5 м3 с надписью \\\"Для ветоши\\\"\",\"В металлические ведра с крышкой и надписью \\\"Для ветоши\\\"\"]', '', 1, ''),
(89, 186074, 'В какой срок для организации ознакомления диспетчерского персонала с особенностями функционирования объектов электроэнергетики субъект оперативно-диспетчерского управления должен в письменной форме направить уведомление о необходимости такого ознакомления в организацию, эксплуатирующую такие объекты электроэнергетики, с указанием их перечня?', '[\"Не позднее 3 рабочих дней до даты ознакомления\",\"Не позднее 5 рабочих дней до даты ознакомления\",\"Не позднее 7 рабочих дней до даты ознакомления\",\"Не позднее 10 рабочих дней до даты ознакомления\"]', '', 1, ''),
(90, 186075, 'Что принимается за начало и конец воздушной линии?', '[\"Первая и последняя анкерные опоры линии\",\"Первая и последняя промежуточные опоры линии\",\"Линейные порталы или линейные вводы электроустановки, служащей для приема и распределения электроэнергии и содержащей коммутационные аппараты, сборные и соединительные шины, вспомогательные устройства (компрессорные, аккумуляторные), а также устройства защиты, автоматики и измерительные приборы, а для ответвлений - ответвительная опора и линейный портал или линейный ввод распределительного устройства\",\"Шинные порталы электроустановки, служащей для приема и распределения электроэнергии и содержащей коммутационные аппараты, сборные и соединительные шины, вспомогательные устройства\"]', '', 3, ''),
(91, 186077, 'Каковы требования Правил по охране труда при эксплуатации электроустановок при выполнении на воздушной линии электропередачи, находящейся под напряжением, работ по удалению с проводов упавших деревьев?', '[\"Допускается выполнять с применением изолирующих штанг\",\"Допускается выполнять с применением диэлектрических перчаток\",\"Допускается выполнять с применением диэлектрических перчаток и бот\",\"Работы выполнять не допускается\"]', '', 4, ''),
(92, 186078, 'Какой персонал относится к электротехнологическому?', '[\"Персонал, который проводит ремонт и обслуживание электроустановок\",\"Персонал, который проводит монтаж, наладку и испытание электротехнологического оборудования\",\"Персонал, который проводит обслуживание электротехнологических установок, и использует в работе электрические машины, переносной электроинструмент и светильники\",\"Персонал, который не попадает под определение электротехнического\"]', '', 3, ''),
(93, 186079, 'Как часто должны проводиться осмотр и проверка исправности аварийного освещения?', '[\"1 раз в полгода\",\"1 раз в год\",\"1 раз в 2 года\",\"1 раз в 3 года\"]', '', 1, ''),
(94, 186080, 'Какие помещения, согласно ПУЭ, относятся к влажным?', '[\"Помещения, в которых относительная влажность воздуха больше 60 %, но не превышает 75 %\",\"Помещения, в которых относительная влажность воздуха в пределах 80 %\",\"Помещения, в которых относительная влажность воздуха больше 75 %, но не превышает 90 %\",\"Помещения, в которых относительная влажность воздуха близка к 100 %\"]', '', 1, ''),
(95, 186081, 'Являются ли лакокрасочные покрытия изоляцией, защищающей от поражения электрическим током?', '[\"Не являются\",\"Являются\",\"Не являются, за исключением случаев, специально оговоренных техническими условиями на конкретные изделия\"]', '', 3, ''),
(96, 186082, 'На какой максимальной высоте над уровнем пола, согласно Правилам устройства электроустановок, должны устанавливаться светильники, обслуживаемые со стремянок или приставных лестниц?', '[\"На высоте не более 3 м до низа светильника\",\"На высоте не более 6 м до узла крепления светильника\",\"На высоте не более 5 м до низа светильника\",\"На высоте не более 7 м до узла крепления светильника\"]', '', 3, ''),
(97, 186083, 'Какие электроустановки и электрические приборы подлежат отключению по окончании рабочего времени?', '[\"Дежурное освещение\",\"Установки пожаротушения и противопожарного водоснабжения\",\"Установки пожарной и охранно-пожарной сигнализации\",\"Электроустановки и бытовые электроприборы в помещениях, в которых по окончании рабочего времени отсутствует дежурный персонал\"]', '', 4, ''),
(98, 186084, 'Какое минимальное время для ознакомления диспетчерского персонала с особенностями функционирования объекта электроэнергетики определяется по согласованию между субъектом оперативно-диспетчерского управления и организацией (ее филиалом), эксплуатирующей такой объект электроэнергетики?', '[\"Не менее 1 рабочего дня\",\"Не менее 3 рабочих дней\",\"Не менее 5 рабочих дней\",\"Не менее 4 рабочих дней\"]', '', 1, ''),
(99, 186085, 'Каким образом не допускается производство работ в действующих электроустановках?', '[\"По наряду-допуску\",\"По распоряжению\",\"На основании перечня работ, выполняемых в порядке текущей эксплуатации\",\"Самовольно\"]', '', 4, ''),
(100, 186086, 'Допустимо ли пребывание одного или нескольких членов бригады отдельно от производителя работ, в случае рассредоточения членов бригады по разным рабочим местам?', '[\"Недопустимо в любом случае\",\"Допустимо в любом случае\",\"Допустимо при наличии у членов бригады III группы по электробезопасности\",\"Допустимо при проведении соответствующего инструктажа\"]', '', 3, ''),
(101, 186087, 'Допускается ли производителю работ совмещать обязанности допускающего согласно Правилам по охране труда при эксплуатации электроустановок?', '[\"Допускается, в этом случае подготовку рабочего места он должен выполнять с одним из членов бригады, имеющим группу III по электробезопасности\",\"Работы выполнять не допускается\"]', '', 1, ''),
(102, 186088, 'Какие виды инструктажа проводятся с административно-техническим персоналом?', '[\"Вводный и целевой (при необходимости) инструктажи по охране труда\",\"Вводный, первичный на рабочем месте, повторный, внеплановый и целевой инструктажи по охране труда, а также инструктаж по пожарной безопасности\",\"Вводный, первичный на рабочем месте, повторный, внеплановый и целевой инструктажи по охране труда\"]', '', 1, ''),
(103, 186089, 'Когда проводится внеочередная проверка знаний персонала?', '[\"При введении в действие у Потребителя новых или переработанных норм и правил\",\"По требованию органов государственного надзора и контроля\",\"При проверке знаний после получения неудовлетворительной оценки\",\"При перерыве в работе в данной должности более 6 месяцев\",\"В любом из перечисленных случаев\"]', '', 5, ''),
(104, 186090, 'Какие помещения, согласно ПУЭ, называются сухими?', '[\"Помещения, в которых относительная влажность воздуха не превышает 60 %\",\"Помещения, в которых относительная влажность воздуха не превышает 75 %\",\"Помещения, в которых относительная влажность воздуха не превышает 70 %\",\"Помещения, в которых относительная влажность воздуха близка к 65 %\"]', '', 1, ''),
(105, 186091, 'Каким должно быть сопротивление заземляющего устройства, к которому присоединены выводы источника трансформатора, при линейном напряжении 380 В источника трехфазного тока?', '[\"Не более 2 Ом\",\"Не более 4 Ом\",\"Не более 6 Ом\",\"Не более 8 Ом\"]', '', 2, ''),
(106, 186092, 'На какой высоте, как правило, должны устанавливаться штепсельные розетки на номинальный ток до 16 А и напряжение до 250 В в производственных помещениях?', '[\"На высоте до 1,0 м\",\"На высоте 0,8-1,0 м\",\"На высоте до 1,5 м\",\"На высоте более 1,5 м\"]', '', 2, ''),
(107, 186093, 'С какой периодичностью должна проводиться перекатка пожарных рукавов?', '[\"Не реже 1 раза в год\",\"Не реже 1 раза в 2 года\",\"Не реже 1 раза в 3 года\"]', '', 1, ''),
(108, 186094, 'В какие сроки должна проводится очередная проверка знаний в отношении диспетчерского, оперативного и оперативно-ремонтного персонала, работников из числа административно-технического персонала?', '[\"Не реже 1 раза в 12 месяцев\",\"Не реже 1 раза в 2 месяца\",\"Не реже 1 раза в 6 месяцев\",\"Не реже 1 раза в 4 месяца\"]', '', 1, ''),
(109, 186095, 'Допускается ли самовольное проведение работ в действующих электроустановках, а также расширение рабочих мест и объема задания, определенных наряд-допуском, распоряжением или утвержденным работодателем перечнем работ, выполняемых в порядке текущей эксплуатации?', '[\"Допускается самовольное проведение работ только при возникновении аварийной ситуации с разрешения вышестоящего оперативного персонала\",\"Допускается расширение рабочих мест и объема задания, определенных наряд-допуском или распоряжением при выполнении неотложных работ, для выполнения которых требуется более 1 часа, с разрешения производителя работ\",\"Допускается, учитывая важность электроустановки в технологическом процессе, с обязательной записью в оперативном журнале\",\"Самовольное проведение работ, расширение рабочих мест и объема задания, определенных наряд-допуском, распоряжением или утвержденным работодателем перечнем работ, выполняемых в порядке текущей эксплуатации в действующих электроустановках не допускается\"]', '', 4, ''),
(110, 186096, 'Кому разрешается работать единолично в электроустановках напряжением до 1000 В, расположенных в помещениях, кроме особо опасных?', '[\"Работнику, имеющему группу IV по электробезопасности\",\"Работнику, имеющему группу III по электробезопасности и право быть производителем работ\",\"Работнику, имеющему группу III по электробезопасности\",\"Работать единолично не разрешается\"]', '', 2, ''),
(111, 186097, 'На какое расстояние не допускается приближаться незащищенными от поражения электрическим током частями тела к токоведущим частям, находящихся под напряжением, при выполнении работ методом «в изоляции»?', '[\"Менее 100 мм\",\"Менее 150 мм\",\"Менее 120 мм\"]', '', 2, ''),
(112, 186098, 'Какие виды инструктажа проводятся с оперативным и оперативно-ремонтным персоналом?', '[\"Вводный и целевой (при необходимости) инструктажи по охране труда\",\"Вводный, первичный на рабочем месте, повторный, внеплановый и целевой инструктажи по охране труда, а также инструктаж по пожарной безопасности\",\"Вводный, первичный на рабочем месте, повторный, внеплановый и целевой инструктажи по охране труда\"]', '', 2, ''),
(113, 186099, 'Какая группа по электробезопасности должна быть у председателя комиссии по проверке знаний персонала организации с электроустановками до 1000 В?', '[\"Вторая\",\"Третья\",\"Четвертая\",\"Пятая\"]', '', 3, ''),
(114, 186100, 'Каким образом должны быть обозначены нулевые рабочие (нейтральные) проводники в электроустановках?', '[\"Буквой N и голубым цветом\",\"Буквой N и белым цветом\",\"Буквой Н и голубым цветом\",\"Буквой Н и серым цветом\"]', '', 1, ''),
(115, 186101, 'Что может быть использовано в качестве естественных заземлителей?', '[\"Металлические трубы водопровода, проложенные в земле\",\"Трубопроводы канализации\",\"Трубопроводы центрального отопления\",\"Все перечисленные здесь трубы и трубопроводы\"]', '', 1, ''),
(116, 186102, 'Допускается ли, согласно Правилам устройства электроустановок, сооружение встроенных или пристроенных подстанций в спальных корпусах различных учреждений, в школьных и других учебных заведениях?', '[\"Допускается без ограничений\",\"Допускается при выполнении в полном объеме санитарных требований по ограничению уровня шума и вибрации\",\"Допускается, если это согласовано с руководством учреждений и учебных заведений\",\"Не допускается\"]', '', 4, ''),
(117, 186103, 'С какой периодичностью руководитель организации должен обеспечивать проведение проверки работоспособности систем и средств противопожарной защиты объекта?', '[\"Проверка работоспособности проводится только после ремонта водопроводной сети\",\"Проверка работоспособности проводится только после подключения новых потребителей к водопроводной сети\",\"Проверка работоспособности проводится не реже 1 раза в квартал\",\"Проверка работоспособности проводится не реже 1 раза в год\"]', '', 3, ''),
(118, 186104, 'Кем утверждается график очередной проверки знаний в организации?', '[\"Председателями комиссий, в которых будет проводиться проверка знаний\",\"Руководителем организации\",\"Инспектор Ростехнадзора\"]', '', 1, '');
INSERT INTO `l4` (`id`, `globalId`, `question`, `answers`, `pravila`, `correctAnswer`, `glava`) VALUES
(119, 186105, 'Что включает в себя понятие \"Наряд-допуск\"?', '[\"Задание на производство работы, устанавливающее условия безопасного проведения работы, состав бригады и ответственных исполнителей\",\"Задание на производство работы, определяющее содержание, место работы и условия безопасного проведения\",\"Задание на производство работы, оформленное на специальном бланке установленной формы и определяющее содержание, место работы, время ее начала и окончания\",\"Задание на производство работы, оформленное на специальном бланке установленной формы и определяющее содержание, место работы, время ее начала и окончания, условия безопасного проведения, состав бригады и работников, ответственных за безопасное выполнение\"]', '', 4, ''),
(120, 186106, 'В каких электроустановках могут выполняться работы в порядке текущей эксплуатации?', '[\"В электроустановках напряжением до 1000 В\",\"В электроустановках напряжением до и выше 1000 В\",\"В любых электроустановках\",\"В электроустановках напряжением не выше 380 В\"]', '', 1, ''),
(121, 186107, 'Какой индекс необходимо указывать при заполнении графы «наименование работ» в поле «Свидетельство на право проведения специальных работ» в удостоверении работника, допущенного к работам под напряжением на токоведущих частях в электроустановках 6-20 кВ?', '[\"И1\",\"И2\",\"И3\"]', '', 3, ''),
(122, 186108, 'В течение какого срока должна проводиться стажировка электротехнического персонала на рабочем месте до назначения на самостоятельную работу?', '[\"От 1 до 5 смен\",\"От 2 до 4 смен\",\"От 2 до 10 смен\",\"От 2 до 14 смен\"]', '', 4, ''),
(123, 186109, 'При каких условиях допускается параллельная работа трансформаторов?', '[\"Соотношение мощностей трансформаторов не более 1:3; коэффициенты трансформации отличаются не более чем на 0,5 %; напряжения короткого замыкания отличаются не более чем на 10 %\",\"Коэффициенты трансформации отличаются не более чем на 0,5 %; напряжения короткого замыкания отличаются не более чем на 10 %; произведена фазировка трансформаторов\",\"Группы соединений обмоток одинаковы; соотношение мощностей трансформаторов не более 1:3; коэффициенты трансформации отличаются не более чем на 0,5 %; напряжения короткого замыкания отличаются не более чем на 10 %\",\"Группы соединений обмоток одинаковы; соотношение мощностей трансформаторов не более 1:3; коэффициенты трансформации отличаются не более чем на 0,5 %; напряжения короткого замыкания отличаются не более чем на 10 %; произведена фазировка трансформаторов\"]', '', 4, ''),
(124, 186110, 'Каким образом обозначаются проводники защитного заземления, а также нулевые защитные проводники в электроустановках напряжением до 1 кВ с глухозаземленной нейтралью?', '[\"Обозначаются PE и имеют цветовое обозначение чередующимися продольными или поперечными полосами одинаковой ширины желтого и зеленого цветов\",\"Обозначаются RE и имеют цветовое обозначение чередующимися продольными или поперечными полосами одинаковой ширины белого и зеленого цветов\",\"Обозначаются PE и имеют цветовое обозначение чередующимися продольными или поперечными полосами одинаковой ширины желтого и белого цветов\"]', '', 1, ''),
(125, 186111, 'Какое минимальное сечение должен иметь медный заземляющий проводник, присоединяющий заземлитель рабочего (функционального) заземления к главной заземляющей шине в электроустановках напряжением до 1 кВ?', '[\"2,5 мм2\",\"6 мм2\",\"7,5 мм2\",\"10 мм2\"]', '', 4, ''),
(126, 186112, 'В каких случаях, согласно Правилам устройства электроустановок, допускается размещение встроенных и пристроенных подстанций с использованием сухих трансформаторов в жилых зданиях при выполнении в полном объеме санитарных требований по ограничению уровня шума и вибрации в соответствии с действующими стандартами?', '[\"В любых случаях по согласованию с управляющей компанией или ТСЖ\",\"В любых случаях по согласованию с органами муниципальной власти\",\"В исключительных случаях по согласованию с жильцами\",\"В исключительных случаях по согласованию с органами государственного надзора\"]', '', 4, ''),
(127, 186113, 'При каком условии разрешается использовать запас воды, предназначенный для нужд пожаротушения?', '[\"При условии, что вода используется для хозяйственных нужд в количестве не более 10 л\",\"При условии, что вода используется для производственных целей с последующим восстановлением израсходованного количества воды\",\"При условии, что вода используется для производственных целей в случае аварийного прекращения подачи воды по трубопроводу\",\"Использовать для хозяйственных и (или) производственных целей запас воды, предназначенный для нужд пожаротушения, запрещается\"]', '', 4, ''),
(128, 186114, 'Какое количество членов постояннодействующей комиссии должно присутствовать при проверке знаний филиала, представительства, структурного подразделения организации?', '[\"Не менее 2 членов постоянно действующей комиссии по проверке знаний филиала, представительства, структурного подразделения организации\",\"Не менее 3 членов постоянно действующей комиссии по проверке знаний филиала, представительства, структурного подразделения организации\",\"Не менее 4 членов постоянно действующей комиссии по проверке знаний филиала, представительства, структурного подразделения организации\",\"Не менее 5 членов постоянно действующей комиссии по проверке знаний филиала, представительства, структурного подразделения организации\"]', '', 2, ''),
(129, 186115, 'Каким образом должно оформляться согласование работ, выполняемых в месте проведения работ по другому наряду-допуску?', '[\"Согласование обсуждается в устной форме между производителем работ и допускающим после подготовки рабочего места по второму наряду-допуску\",\"Время и способ согласования определяет ответственный руководитель работ\",\"Выполнение работ в месте проведения работ по другому наряду-допуску должно проводиться под наблюдением ответственного руководителя\",\"Согласование оформляется до начала подготовки рабочего места по второму наряд-допуску записью \\\"Согласовано\\\" на лицевой стороне второго наряд-допуска-допуска, располагаемой в левом нижнем поле документа с подписями работников, согласующих документ\"]', '', 4, ''),
(130, 186116, 'Какие работы из перечисленных можно отнести к работам, выполняемым в порядке текущей эксплуатации в электроустановках напряжением до 1000 В?', '[\"Снятие и установку электросчетчиков, других приборов и средств измерений\",\"Ремонт пусковой и коммутационной аппаратуры, установленной на щитках\",\"Замену ламп и чистку светильников на высоте более 2,5 м\",\"Любые из перечисленных работ\"]', '', 1, ''),
(131, 186117, 'При совместном производстве нескольких видов работ, по которым требуется оформление наряда-допуска, допускается ли оформление единого наряда-допуска?', '[\"Да\",\"Нет\"]', '', 1, ''),
(132, 186118, 'В течение какого срока проводится дублирование перед допуском электротехнического персонала к самостоятельной работе?', '[\"От 1 до 5 смен\",\"От 2 до 4 смен\",\"От 2 до 12 смен\",\"От 2 до 14 смен\"]', '', 3, ''),
(133, 186119, 'В каком случае на трансформаторах с системой охлаждения Д электродвигатели вентиляторов должны автоматически включаться?', '[\"При температуре масла 45 °С или токе, равном номинальному, независимо от температуры масла\",\"При температуре масла 50 °С или токе, равном номинальному\",\"При температуре масла 55 °С или токе, равном номинальному, независимо от температуры масла\",\"При температуре масла 60 °С или токе, равном номинальному\"]', '', 3, ''),
(134, 186120, 'Каким цветом должны быть обозначены шины трехфазного тока?', '[\"Шины фазы A - зеленым, фазы B - желтым, фазы C - красным цветом\",\"Шины фазы A - зеленым, фазы B - красным, фазы C - желтым цветом\",\"Шины фазы A - желтым, фазы B - зеленым, фазы C - красным цветом\",\"Шины фазы A - красным, фазы B - зеленым, фазы C - желтым цветом\"]', '', 3, ''),
(135, 186121, 'Что может использоваться в качестве РЕ-проводников в электроустановках напряжением до 1000 В?', '[\"Стальные трубы электропроводок\",\"Свинцовые оболочки проводов и кабелей\",\"Водопроводные трубы при наличии в них изолирующих вставок\",\"Трубы канализации и центрального отопления\"]', '', 1, ''),
(136, 186122, 'Какое минимальное расстояние, согласно Правилам устройства электроустановок, должно быть от места установки ВУ, ВРУ, ГРЩ до трубопроводов (водопровод, отопление, канализация, внутренние водостоки)?', '[\"Расстояние не менее 0,5 м\",\"Расстояние не менее 1,0 м\",\"Расстояние не менее 2,0 м\",\"Расстояние не менее 3,5 м\"]', '', 2, ''),
(137, 186123, 'С какой периодичностью производится проверка работоспособности систем оповещения людей о пожаре?', '[\"Не реже 1 раза в месяц\",\"Это должны делать ответственные за пожарную безопасность в организации каждые 6 месяцев\",\"Не реже 1 раза в квартал\",\"Не реже 1 раза в год\"]', '', 3, ''),
(138, 186124, 'Каким образом проводится проверка знаний каждого работника?', '[\"Индивидуально\",\"Группой по 3 человека\",\"Группой по 2 человека\",\"Группой до 10 человек\"]', '', 1, ''),
(139, 186125, 'Что недопустимо при выполнении работ под напряжением в электроустановках напряжением до 1000 В?', '[\"Ограждать токоведущие части, находящиеся под напряжением, к которым возможно случайное прикосновение\",\"Пользоваться изолированным инструментом, применять диэлектрические галоши и перчатки\",\"Работать в одежде с короткими или засученными рукавами, а также использовать ножовки, напильники, металлические метры и другие металлические инструменты и приспособления, не предназначенные для выполнения работ под напряжением\"]', '', 3, ''),
(140, 186126, 'Какие из перечисленных мероприятий необходимо учитывать при оформлении перечня работ, выполняемых в порядке текущей эксплуатации?', '[\"Только условия безопасности и возможности единоличного выполнения конкретных работ\",\"Только квалификацию персонала\",\"Только степень важности электроустановки в целом или ее отдельных элементов в технологическом процессе\",\"Необходимо учитывать все перечисленные мероприятия\"]', '', 4, ''),
(141, 186127, 'Допускается ли закреплять строп страховочной привязи поддерживающих и натяжных многоцепных изолирующих подвесках за гирлянду изолятора?', '[\"Допускается закреплять за одну из гирлянд изоляторов, на которой работа не ведется\",\"Допускается закреплять за любую гирлянду\",\"Не допускается\"]', '', 1, ''),
(142, 186128, 'На какой срок может быть продлено для работника дублирование, если за отведенное время он не приобрел достаточных производственных навыков?', '[\"От 1 до 5 смен\",\"От 2 до 4 смен\",\"От 2 до 12 смен\",\"От 2 до 14 смен\",\"От 5 до 15 смен\"]', '', 3, ''),
(143, 186129, 'В какие сроки должен проводиться осмотр трансформаторов на трансформаторных пунктах без их отключения?', '[\"Не реже 1 раза в сутки\",\"Не реже 1 раза в неделю\",\"Не реже 1 раза в месяц\",\"Не реже 1 раза в год\"]', '', 3, ''),
(144, 186130, 'Как обозначаются шины при переменном однофазном токе?', '[\"Шина B, присоединенная к концу обмотки источника питания, - красным цветом, шина A, присоединенная к началу обмотки источника питания, - желтым цветом\",\"Шина B, присоединенная к концу обмотки источника питания, - зеленым цветом, шина A, присоединенная к началу обмотки источника питания, - желтым цветом\",\"Шина А, присоединенная к концу обмотки источника питания, - синим цветом, шина В, присоединенная к началу обмотки источника питания, - зеленым цветом\",\"Шина B, присоединенная к концу обмотки источника питания, - голубым цветом, шина A, присоединенная к началу обмотки источника питания, - зеленым цветом\"]', '', 1, ''),
(145, 186131, 'Каким должно быть минимальное сечение отдельно проложенных защитных алюминиевых проводников?', '[\"2,5 мм2\",\"4 мм2\",\"8 мм2\",\"14 мм2\",\"16 мм2\"]', '', 5, ''),
(146, 186132, 'Каким, согласно Правилам устройства электроустановок, должно быть сечение РЕ проводников, не входящих в состав кабеля?', '[\"Не менее 1,5 мм2 - при наличии механической защиты и 2 мм2 - при ее отсутствии\",\"Не менее 2,5 мм2 - при наличии механической защиты и 4 мм2 - при ее отсутствии\",\"Не менее 4 мм2 - при наличии механической защиты и 6 мм2 - при ее отсутствии\",\"Не менее 7,5 мм2 - при наличии механической защиты и 5,5 мм2 - при ее отсутствии\"]', '', 2, ''),
(147, 186133, 'Укажите, в какие сроки проводится проверка знаний вновь назначенных на должность работников, относящихся к категории административно-технического персонала или вспомогательного персонала?', '[\"Hе позднее 1 месяца после назначения на должность\",\"Не позднее cрока, определенного программой подготовки на должность вновь назначенного работника\",\"Не позднее cрока, определенного приказом по предприятию в зависимости от квалификации вновь назначенного работника\"]', '', 1, ''),
(148, 186134, 'При каком значении процентного отношения неправильных ответов к общему количеству вопросов считается \"неудовлетворительно\" при проверке знаний работника?', '[\"Более 30 %\",\"Более 20 %\",\"Более 40 %\",\"Более 50 %\"]', '', 1, ''),
(149, 186135, 'Какие мероприятия не относятся к организационным, обеспечивающим безопасность работ в электроустановках?', '[\"Выдача разрешения на подготовку рабочего места и на допуск к работе\",\"Оформление работ нарядом-допуском, распоряжением или перечнем работ, выполняемых в порядке текущей эксплуатации\",\"Допуск к работе, надзор во время работы, оформление перерыва в работе, перевода на другое место, окончания работы\",\"Производство необходимых отключений и принятие мер, препятствующих подаче напряжения на место работы вследствие ошибочного или самопроизвольного включения коммутационных аппаратов\"]', '', 4, ''),
(150, 186136, 'Что обязан сделать допускающий, осуществляющий первичный допуск бригады к работе по наряд-допуску или распоряжению?', '[\"Допускающий должен проверить подготовку рабочего места\",\"Проверить соответствие состава бригады, указанного в наряде-допуске или распоряжении по именным удостоверениям\",\"Доказать бригаде, что напряжение отсутствует, показом установленных заземлений или проверкой отсутствия напряжения, если заземления не видны с рабочего места\",\"Доказать бригаде, что напряжение отсутствует, показом установленных заземлений, а в электроустановках напряжением 35 кВ и ниже - прикоснувшись рукой к токоведущим частям\",\"Все перечисленное\"]', '', 5, ''),
(151, 186137, 'Сколько схем существует для обеспечения безопасности персонала при выполнении работ под напряжением на токоведущих частях согласно Правилам по охране труда при эксплуатации электроустановок?', '[\"Одна\",\"Две\",\"Три\"]', '', 3, ''),
(152, 186138, 'Какие меры принимаются к работнику, который в период дублирования был признан профнепригодным к данному виду деятельности?', '[\"Он подлежит увольнению\",\"Он снимается с подготовки\",\"Ему продлевается срок дублирования на определенное количество смен\",\"Может быть применена одна из перечисленных мер\"]', '', 2, ''),
(153, 186139, 'В каком из перечисленных случаев проводятся внеочередные осмотры трансформаторов?', '[\"После грозы и сильного ветра\",\"При работе газовой защиты на сигнал\",\"При отключении трансформатора газовой или дифференциальной защитой\",\"Во всех перечисленных случаях\"]', '', 4, ''),
(154, 186140, 'Как обозначаются шины при постоянном токе?', '[\"Положительная шина (+) - красным цветом, отрицательная (-) - синим и нулевая рабочая M - голубым цветом\",\"Положительная шина (+) - синим цветом, отрицательная (-) - красным и нулевая рабочая M - голубым цветом\",\"Положительная шина (+) - зеленым цветом, отрицательная (-) - красным и нулевая рабочая M - голубым цветом\",\"Положительная шина (+) - желтым цветом, отрицательная (-) - зеленым и нулевая рабочая M - голубым цветом\"]', '', 1, ''),
(155, 186141, 'Каким образом должно быть выполнено присоединение заземляющих и нулевых защитных проводников, и проводников уравнивания потенциалов к открытым проводящим частям?', '[\"Только при помощи сварки\",\"При помощи болтовых соединений или сварки\",\"При помощи болтовых соединений или гибких проводников\"]', '', 2, ''),
(156, 186142, 'Что используется при присоединении переносной или передвижной электросварочной установки непосредственно к стационарной электрической сети?', '[\"Автоматический выключатель и предохранители\",\"Коммутационный и защитный аппараты (аппарат) с разборными или разъемными контактными соединениями\",\"Измерительный прибор\"]', '', 2, ''),
(157, 186143, 'От каких факторов зависит необходимость и длительность каждого этапа подготовки по новой должности оперативного персонала?', '[\"От всего перечисленного\",\"От уровня профессионального образования работника и уровня его знаний\",\"От стажа практической работы по смежным должностям\",\"От технической сложности объекта\"]', '', 1, ''),
(158, 186144, 'Каким образом выставляется итоговая оценка при прохождении проверки знаний с использованием программного обеспечения и получения неудовлетворительной оценки автоэкзаменатора?', '[\"Устанавливаться комиссией по проверке знаний по результатам устного опроса работника\",\"Проводится дополнительное тестирование знаний с использованием программного обеспечения через 3 дня\",\"Проводится дополнительное тестирование знаний с использованием программного обеспечения через 7 дней\",\"Проводится дополнительное тестирование знаний с использованием программного обеспечения через 10 дней\"]', '', 1, ''),
(159, 186145, 'Какие из перечисленных работников являются ответственными за безопасное ведение работ в электроустановках?', '[\"Только выдающий наряд-допуск, отдающий распоряжение, утверждающий перечень работ, выполняемых в порядке текущей эксплуатации\",\"Только ответственный руководитель работ, допускающий, производитель работ, наблюдающий и член бригады\",\"Все перечисленные работники\",\"Все перечисленные работники, а так же выдающий разрешение на подготовку рабочего места и на допуск в случаях, определенных в пункте 5.14 Правил\"]', '', 4, ''),
(160, 186146, 'Какой инструктаж должен пройти электротехнический персонал перед началом работ по распоряжению?', '[\"Первичный на рабочем месте\",\"Вводный\",\"Целевой\",\"Повторный\"]', '', 3, ''),
(161, 186147, 'Какую группу по электробезопасности должны иметь ответственный руководитель работ и производитель работ, имеющие право выполнения работ под напряжением на токоведущих частях, при работах в электроустановках до 1000 В?', '[\"Не ниже IV\",\"Не ниже III\",\"Ответственный руководитель работ не ниже IV, а производитель - III\"]', '', 1, ''),
(162, 186148, 'Какая периодичность проверки знаний по электробезопасности установлена для электротехнического персонала, непосредственно организующего и проводящего работы по обслуживанию действующих электроустановок?', '[\"Не реже одного раза в год\",\"Не реже одного раза в два года\",\"Не реже одного раза в три года\",\"Не реже одного раза в пять лет\"]', '', 1, ''),
(163, 186149, 'В каком из перечисленных случаев трансформатор должен быть аварийно выведен из работы?', '[\"Только при сильном неравномерном шуме и потрескивании внутри трансформатора\",\"Только при выбросе масла из расширителя\",\"Только при течи масла с понижением его уровня ниже уровня масломерного стекла\",\"Только при необходимости немедленной замены масла по результатам лабораторных анализов\",\"В любом из перечисленных случаев\"]', '', 5, ''),
(164, 186150, 'Что, согласно Правилам устройства электроустановок, называется приемником электрической энергии (электроприемником)?', '[\"Распределительное устройство, предназначенное для обеспечения потребителей электрической энергией\",\"Подстанция, работающая на определенной территории\",\"Электроустановка, предназначенная для обеспечения потребителей электрической энергией\",\"Аппарат, агрегат и др., предназначенный для преобразования электрической энергии в другой вид энергии\"]', '', 4, ''),
(165, 186151, 'Что может быть применено для защиты при косвенном прикосновении в цепях, питающих переносные электроприемники?', '[\"Автоматическое отключение питания\",\"Защитное электрическое разделение цепей\",\"Сверхнизкое напряжение\",\"Двойная изоляция\",\"Любая из перечисленных мер защиты, в зависимости от категории помещения по уровню опасности поражения людей электрическим током\"]', '', 5, ''),
(166, 186152, 'Какая должна быть длина гибкого кабеля, соединяющего источник сварочного тока и коммутационный аппарат?', '[\"Не больше 15 м\",\"Не больше 30 м\",\"Не больше 10 м\"]', '', 1, ''),
(167, 186153, 'Чем определяется объем знаний для проверки по каждой должности требованиями \"Правил работы с персоналом в организациях электроэнергетики Российской Федерации\"?', '[\"Должностными обязанностями (трудовыми функциями)\",\"Определяется руководителем организации\",\"Определяется руководителем подразделения, ответственного за работу с кадрами\"]', '', 1, ''),
(168, 186154, 'Каким образом фиксируются результаты проверки знаний?', '[\"Формируется приказ\",\"Оформляется протоколом проверки знаний и удостоверением и фиксируется в журнале учета проверки знаний\",\"Формируется акт о проведении и получении соответствующих результатов работников\",\"Только удостоверением\"]', '', 2, ''),
(169, 186155, 'При выполнении каких работ выдающий наряд-допуск имеет право не назначать ответственного руководителя работ?', '[\"Под наведенным напряжением\",\"Без снятия напряжения на токоведущих частях с изоляцией человека от земли\",\"В местах пересечения ВЛ с другими ВЛ и транспортными магистралями, в пролетах пересечения проводов в ОРУ\",\"При выполнении работ в РУ напряжением выше 1000 В с одиночной секционированной или несекционированной системой шин, не имеющей обходной системы шин\"]', '', 4, ''),
(170, 186156, 'Что должно предшествовать началу работ по наряд-допуску или по распоряжению?', '[\"Первичный инструктаж на рабочем месте\",\"Вводный инструктаж\",\"Целевой инструктаж\",\"Повторный инструктаж\"]', '', 3, ''),
(171, 186157, 'В каких случаях запрещается выполнение (возобновление) работ на ВЛ, ВЛЗ, ВЛИ под напряжением?', '[\"Аварийного отключения ВЛ, ВЛЗ, ВЛИ действием защит при производстве работ на токоведущих частях\",\"Обнаружения повреждения на ВЛ, ВЛЗ, ВЛИ, устранение которого невозможно без нарушения технологии работ под напряжением на токоведущих частях\",\"Отсутствия или неисправности технических средств и средств защиты\",\"Во всех перечисленных случаях\"]', '', 4, ''),
(172, 186158, 'Где проводится проверка знаний ответственных за электрохозяйство и их заместителей?', '[\"В комиссии организации\",\"В территориальной комиссии Ростехнадзора\",\"В комиссии учебного центра, образованной приказом руководителя учебного центра\",\"В любой из перечисленных комиссий\"]', '', 2, ''),
(173, 186159, 'Какое из положений не соответствует требованиям Правил технической эксплуатации электроустановок потребителей к содержанию помещений распределительных устройств?', '[\"В помещениях РУ двери, окна должны быть всегда закрыты, а проемы в перегородках между аппаратами, содержащими масло, заделаны\",\"Для предотвращения попадания животных и птиц все отверстия и проемы в наружных стенах помещений закрываются сетками с размером ячейки (1 х 1) см\",\"Токоведущие части пускорегулирующих аппаратов и аппаратов защиты должны быть ограждены от случайных прикосновений\",\"Дубликаты ключей от электропомещений должны храниться в операторных соответствующих установок\"]', '', 4, ''),
(174, 186160, 'Что, согласно Правилам устройства электроустановок, называется потребителем электрической энергии?', '[\"Электрическая часть энергосистемы и питающиеся от нее приемники электрической энергии, объединенные общностью процесса передачи и распределения электрической энергии\",\"Электрические и тепловые сети, связанные общностью режимов в непрерывном процессе преобразования, передачи и распределения электрической и тепловой энергии\",\"Электроприемник или группа электроприемников, объединенных технологическим процессом и размещающихся на определенной территории\",\"Системы электроснабжения подземных, тяговых и других специальных установок, связанных общностью технологических процессов\"]', '', 3, ''),
(175, 186161, 'Что не допускается прокладывать совместно в стальных и других механических прочных трубах, рукавах, коробах, лотках и замкнутых каналах строительных конструкций зданий?', '[\"Силовые и контрольные кабели цепей нескольких машин, панелей, щитов, пультов, связанных технологическим процессом\",\"Взаиморезервируемые провода и кабели\",\"Провода и кабели цепей нескольких групп одного вида освещения (рабочего или аварийного) с общим числом проводов в трубе, не более восьми\",\"Провода и кабели осветительных цепей до 42 В с цепями выше 42 В, при условии заключения проводов цепей до 42 В в отдельную изоляционную трубу\"]', '', 2, ''),
(176, 186162, 'При каком напряжении шкафы комплектных устройств и корпуса сварочного оборудования (машин), имеющие неизолированные токоведущие части, должны быть оснащены блокировкой, обеспечивающей при открывании дверей (дверец) отключение от электрической сети устройств, находящихся внутри шкафа (корпуса)?', '[\"Выше 24 В переменного или выше 60 В постоянного тока\",\"Выше 36 В переменного или выше 60 В постоянного тока\",\"Выше 50 В переменного или выше 110 В постоянного тока\"]', '', 3, ''),
(177, 186163, 'В каких случаях проводится внеочередная проверка знаний?', '[\"В любом из перечисленных случаев\",\"При нарушении работниками требований нормативных актов по охране труда\",\"При переводе работника на новую должность (для рабочих - на новое рабочее место)\",\"При перерыве в работе в данной должности более 6 месяцев\"]', '', 1, ''),
(178, 186164, 'В каких случаях не проводится дублирование?', '[\"При подготовке по новой должности - после проверки знаний\",\"После перерыва в работе более 30 календарных дней, но менее 60 календарных дней - в случаях, установленных порядком проведения работы с персоналом, принятым в организации\",\"После перерыва в работе от 60 календарных дней до 6 месяцев\",\"После перерыва в работе 20 календарных дней до 1 месяца\"]', '', 4, ''),
(179, 186165, 'Что входит в обязанности ответственного руководителя при проведении работ в электроустановках?', '[\"Он отвечает за дачу команд по отключению и заземлению оборудования и получению подтверждения их выполнения, а также самостоятельные действия по отключению и заземлению оборудования в соответствии с мероприятиями по подготовке рабочего места, определенными наряд-допуском\",\"Он отвечает за выполнение указанных в наряд-допуске-допуске мероприятий по подготовке рабочего места и их достаточность, за принимаемые им дополнительные меры безопасности, необходимые по условиям выполнения работ, за полноту и качество целевого инструктажа бригады, в том числе проводимого допускающим и производителем работ, а также за организацию безопасного ведения работ\",\"Он отвечает за координацию времени и места допускаемых к работам в электроустановках бригад, в том числе учет бригад, получение информации от всех допущенных к работам в электроустановках бригад\"]', '', 2, ''),
(180, 186166, 'Кто проводит целевой инструктаж при работах по распоряжению для членов бригады?', '[\"Ответственный руководитель работ\",\"Работник, отдающий распоряжение\",\"Производитель работ - членам бригады\",\"Все перечисленные лица\"]', '', 3, ''),
(181, 186167, 'Необходима запись о допуске на подготовленное рабочее место в оперативном журнале согласно Правилам по охране труда при эксплуатации электроустановок?', '[\"Да\",\"Нет\",\"Правилами не оговорено\"]', '', 1, ''),
(182, 186168, 'Сколько человек должно быть в комиссии по проверке знаний электротехнического персонала?', '[\"Не менее трех человек\",\"Не менее четырех человек\",\"Не менее пяти человек\",\"Правилами не регламентируется\"]', '', 3, ''),
(183, 186169, 'В каком случае нарушено требование Правил технической эксплуатации электроустановок потребителей?', '[\"Кабельные каналы и наземные кабельные лотки ОРУ и ЗРУ должны быть закрыты несгораемыми плитами, а места выхода кабелей из кабельных каналов, лотков, с этажей и переходы между кабельными отсеками должны быть уплотнены огнеупорным материалом\",\"На всех ключах, кнопках и рукоятках управления должны быть надписи, указывающие операцию, для которой они предназначены\",\"Исправность резервных элементов РУ (трансформаторов, выключателей, шин и др.) должна регулярно проверяться включением под напряжение в сроки, установленные местными инструкциями\",\"У дежурного персонала должен быть запас калиброванных плавких вставок. Плавкие вставки должны соответствовать типу предохранителей. Применение плавких некалиброванных вставок допускается только в исключительных случаях\"]', '', 4, ''),
(184, 186170, 'Что, согласно Правилам устройства электроустановок, называется нормальным режимом потребителя электрической энергии?', '[\"Режим, при котором обеспечиваются средние значения параметров его работы\",\"Режим, при котором обеспечиваются заданные значения параметров его работы\",\"Режим, при котором обеспечиваются максимальные значения параметров его работы\",\"Режим, при котором обеспечиваются минимальные значения параметров его работы\"]', '', 2, ''),
(185, 186171, 'Что должно быть обеспечено при прокладке проводов и кабелей в трубах, глухих коробах, гибких металлических рукавах и замкнутых каналах?', '[\"Изоляция труб, глухих коробов, гибких металлических рукавов и замкнутых каналов\",\"Возможность замены проводов и кабелей\",\"Защита от атмосферных осадков\"]', '', 2, ''),
(186, 186172, 'Укажите последовательность действий по оценке обстановки и обеспечению безопасных условий для оказания первой помощи (приказ Минздрава России от 04.05.2012 N 477н).', '[\"1) Определение угрожающих факторов для жизни и здоровья пострадавшего; 2) устранение угрожающих факторов для жизни и здоровья; 3) вызов скорой медицинской помощи;4) прекращение действия повреждающих факторов на пострадавшего; 5) оценка количества пострадавших\",\"1) Определение угрожающих факторов для собственной жизни и здоровья; 2) определение угрожающих факторов для жизни и здоровья пострадавшего; 3) устранение угрожающих факторов для жизни и здоровья; 4) прекращение действия повреждающих факторов на пострадавшего; 5) оценка количества пострадавших; 6) извлечение пострадавшего из транспортного средства или других труднодоступных мест; 7) перемещение пострадавшего\",\"1) Вызов скорой медицинской помощи; 2) других специальных служб, сотрудники которых обязаны оказывать первую помощь в соответствии с федеральным законом или со специальным правилом; 3) устранение угрожающих факторов для жизни и здоровья; 4) прекращение действия повреждающих факторов на пострадавшего; 5) оценка количества пострадавших; 6) извлечение пострадавшего из транспортного средства или других труднодоступных мест\"]', '', 2, ''),
(187, 186173, 'Для проведения проверки знаний руководитель организации должен назначить постоянно действующую комиссию организации в составе не менее:', '[\"5 человек\",\"3 человек\",\"7 человек\"]', '', 1, ''),
(188, 186174, 'Какая продолжительность дублирования конкретного работника при подготовке по новой должности?', '[\"Не менее 5 рабочих смен\",\"Не менее 7 рабочих смен\",\"Не менее 12 рабочих смен\",\"Не менее 4 рабочих смен\"]', '', 3, ''),
(189, 186175, 'Кто назначается ответственным руководителем работ в электроустановках выше 1000 В?', '[\"Работники из числа административно-технического персонала, имеющие группу V по электробезопасности\",\"Работники из числа административно-технического персонала, имеющие группу IV по электробезопасности\",\"Работники из числа оперативного персонала, имеющие группу IV по электробезопасности\"]', '', 1, ''),
(190, 186176, 'Кто инструктирует бригаду по вопросам использования инструмента и приспособлений?', '[\"Ответственный руководитель работ\",\"Производитель работ\",\"Допускающий\"]', '', 2, ''),
(191, 186177, 'Что является определением термина \"изолированная нейтраль\"?', '[\"Нейтраль трансформатора или генератора, не присоединенная к заземляющему устройству или присоединенная к нему через большое сопротивление приборов сигнализации, измерения, защиты и других аналогичных им устройств\",\"Нейтраль трансформатора или генератора, присоединенная к заземляющему устройству непосредственно\",\"Нейтраль трансформатора или генератора, присоединенная к заземляющему устройству через активные токоограничивающие сопротивления\"]', '', 1, ''),
(192, 186178, 'Где проходят проверку знаний по электробезопасности члены комиссий структурных подразделений организации?', '[\"В соответствующей комиссии Ростехнадзора\",\"В центральной комиссии Потребителя\",\"В комиссии обучающей организации\",\"В комиссии, указанной Ростехнадзором\"]', '', 2, ''),
(193, 186179, 'Кто должен выполнять уборку помещений распределительных устройств и очистку электрооборудования?', '[\"Обученный персонал с соблюдением правил безопасности\",\"Оперативно-ремонтный персонал, обслуживающий данную установку\",\"Ремонтный персонал с группой по электробезопасности не ниже IV\"]', '', 1, ''),
(194, 186180, 'Что, согласно Правилам устройства электроустановок, называется независимым источником питания?', '[\"Источник питания, на котором сохраняется напряжение в нормальном режиме, при исчезновении его на другом или других источниках питания\",\"Источник питания, на котором сохраняется напряжение в послеаварийном режиме в регламентированных пределах при исчезновении его на другом или других источниках питания\",\"Аппарат, агрегат и др., предназначенный для независимого преобразования электрической энергии в другой вид энергии\",\"Электрическая часть энергосистемы, питающая приемники электрической энергии, размещающиеся на определенной территории\"]', '', 2, ''),
(195, 186181, 'Что должно учитываться при выборе вида электропроводки и способа прокладки проводов и кабелей?', '[\"Требования охраны окружающей среды\",\"Требования электробезопасности и пожарной безопасности\",\"Экспертное мнение главного энергетика и технолога проектной и эксплуатирующей организации\"]', '', 2, ''),
(196, 186182, 'Укажите верный перечень исчерпывающих мероприятий по оказанию первой помощи. (приказ Минздрава России от 04.05.2012 N 477н).', '[\"1) Оценка обстановки и обеспечение безопасных условий для оказания первой помощи;\\n2) вызов скорой медицинской помощи, других специальных служб, сотрудники которых обязаны оказывать первую помощь;\\n3) определение наличия сознания у пострадавшего;\\n4) восстановление проходимости дыхательных путей и определение признаков жизни у пострадавшего;\\n5) проведение сердечно-легочной реанимации до появления признаков жизни;\\n6) поддержание проходимости дыхательных путей;\\n7) осмотр пострадавшего и временная остановка наружного кровотечения;\\n8) подробный осмотр пострадавшего в целях выявления признаков травм, отравлений и других состояний, угрожающих его жизни и здоровью и оказание первой помощи;\\n9) придание пострадавшему оптимального положения тела;\\n10) контроль состояния пострадавшего (сознание, дыхание, кровообращение) и оказание психологической поддержки;\\n11) передача пострадавшего бригаде скорой медицинской помощи, другим специальным службам, сотрудники которых обязаны оказывать первую помощь\",\"1) Вызов скорой медицинской помощи, других специальных служб, сотрудники которых обязаны оказывать первую помощь;\\n2) определение наличия сознания у пострадавшего;\\n3) восстановление проходимости дыхательных путей и определение признаков жизни у пострадавшего;\\n4) проведение сердечно-легочной реанимации до появления признаков жизни;\\n5) осмотр пострадавшего и временная остановка наружного кровотечения;\\n6) придание пострадавшему оптимального положения тела;\\n7) передача пострадавшего бригаде скорой медицинской помощи, другим специальным службам, сотрудники которых обязаны оказывать первую помощь\",\"1) Убедиться в отсутствии пульса на сонной артерии;\\n2) убедиться в отсутствии признаков дыхания;\\n3) освободить грудную клетку от одежды и расстегнуть поясной ремень;\\n4) прикрыть двумя пальцами мечевидный отросток;\\n5) нанести удар кулаком по грудине;\\n6) проверить пульс, при отсутствии пульса перейти к непрямому массажу сердца\"]', '', 1, ''),
(197, 186183, 'В какой срок лицо, получившее неудовлетворительную оценку по результатам проверки знаний, должно пройти повторную проверку?', '[\"Не более 1 месяца\",\"Не позднее 2 недель\",\"Вопрос о сроках повторной проверки и о возможности сохранения трудового договора с работником решается руководителем организации\"]', '', 1, ''),
(198, 186184, 'Какая продолжительность дублирования конкретного работника после перерыва в работе более 30 календарных дней?', '[\"Не менее 1 рабочий смены\",\"Не менее 7 рабочих смен\",\"Не менее 12 рабочих смен\",\"Не менее 4 рабочих смен\"]', '', 1, ''),
(199, 186185, 'Кто назначается ответственным руководителем работ в электроустановках до 1000 В?', '[\"Работники из числа оперативного персонала, имеющие группу IV по электробезопасности\",\"Работники из числа административно-технического персонала, имеющие группу III по электробезопасности\",\"Работники из числа административно-технического персонала, имеющие группу IV по электробезопасности\"]', '', 3, ''),
(200, 186186, 'Кому проводит целевой инструктаж, предусматривающий указания по безопасному выполнению конкретной работы, выдающий наряд-допуск?', '[\"Ответственному руководителю работ или, если ответственный руководитель не назначается, производителю работ (наблюдающему)\",\"Допускающему, ответственному руководителю работ, производителю работ (наблюдающему)\",\"Производителю работ (наблюдающему) и членам бригады\",\"Ответственному руководителю работ, производителю работ (наблюдающему) и членам бригады\"]', '', 1, ''),
(201, 186187, 'Какая электроустановка считается действующей?', '[\"Исправная электроустановка\",\"Электроустановка или ее часть, которая находится под напряжением, либо на которую напряжение может быть подано включением коммутационных аппаратов\",\"Электроустановка, которая находится в постоянной эксплуатации\",\"Электроустановка, которая находится под напряжением не ниже 220 В\"]', '', 2, ''),
(202, 186188, 'Сколько человек должно присутствовать в комиссии по проверке знаний членов комиссий структурных подразделений организации?', '[\"Не менее трех человек, в том числе председатель или его заместитель\",\"Не менее четырех человек, в том числе председатель или его заместитель, а также секретарь\",\"Не менее пяти человек\",\"Правилами не оговаривается\"]', '', 1, ''),
(203, 186189, 'Кто у Потребителя утверждает график периодических осмотров воздушных линий?', '[\"Ответственный за электрохозяйство Потребителя\",\"Технический руководитель Потребителя\",\"Руководитель Потребителя\",\"С одной стороны - руководитель Потребителя, с другой стороны - инспектор Ростехнадзора\"]', '', 1, ''),
(204, 186190, 'На основании чего, согласно Правилам устройства электроустановок, определяются категории электроприемников по надежности электроснабжения в процессе проектирования системы электроснабжения?', '[\"На основании загруженности электрической сети и перегрузочной способности элементов электроприемников\",\"На основании возможности технологического резервирования и текущего режима, в котором находится потребитель электрической энергии\",\"На основании нормативной документации и технологической части проекта\",\"На основании требований соответствующих глав ПУЭ и применяющегося режима заземления нейтралей\"]', '', 3, ''),
(205, 186191, 'Какие провода следует применять при наличии масел и эмульсий в местах их прокладки?', '[\"Защищенные провода в оболочке из трудносгораемых материалов\",\"Провода с маслостойкой изоляцией\",\"Провода с резиновой изоляцией в оплетке из хлопчатобумажной или синтетической пряжи\"]', '', 2, ''),
(206, 186192, 'Укажите последовательность действий по восстановлению проходимости дыхательных путей и определению признаков жизни у пострадавшего. (приказ Минздрава России от 04.05.2012 N 477н).', '[\"1) Удалить слизь и содержимое желудка;\\n2) приподнять ноги и расстегнуть поясной ремень, при возможности положить холод на живот\",\"1) Запрокинуть голову с подъемом подбородка;\\n2) выдвинуть нижнюю челюсть;\\n3) определить наличие дыхания с помощью слуха, зрения и осязания;\\n4) определить наличие кровообращения, проверить пульс на магистральных артериях\",\"1) Убедиться в отсутствии пульса на сонной артерии;\\n2) убедиться в отсутствии признаков дыхания;\\n3) освободить грудную клетку от одежды и расстегнуть поясной ремень\"]', '', 2, ''),
(207, 186193, 'Каков порядок допуска к самостоятельной работе вновь принятых работников или имевших перерыв в работе более 6 месяцев?', '[\"В зависимости от категории персонала, после прохождения этапов подготовки в соответствии с индивидуальной программой подготовки\",\"В зависимости от категории персонала, после прохождения специальной подготовки, программу и порядок проведения которой определяет руководитель организации\",\"В зависимости от категории персонала, после ознакомления с изменениями в схемах и режимах работы энергоустановок, с вновь введенными в действие HТД, приказами и распоряжениями\"]', '', 1, ''),
(208, 186194, 'Кто несет ответственность за действия работника, допущенного к дублированию на рабочем месте?', '[\"Сам работник, допущенный к дублированию, так и работник, под руководством и контролем которого проводится дублирование\",\"Только сам работник\",\"Руководитель организации\",\"Инспектор Ростехнадзора\"]', '', 1, ''),
(209, 186195, 'За что отвечает допускающий?', '[\"За достаточность и правильность указанных в наряд-допуске\",\"За правильность и достаточность принятых им мер безопасности по подготовке рабочих мест и соответствие их мероприятиям, указанным в наряд-допуске или распоряжении, характеру и месту работы, за правильный допуск к работе, а также за полноту и качество проводимого им целевого инструктажа\",\"За возможность безопасного осуществления отключения, включения и заземления оборудования, находящегося в его управлении\"]', '', 2, ''),
(210, 186196, 'Кому проводит целевой инструктаж, предусматривающий указания по безопасному выполнению конкретной работы, отдающий распоряжение?', '[\"Допускающему и членам бригады (исполнителям)\",\"Производителю (наблюдающему) или непосредственному исполнителю работ\",\"Ответственному руководителю и производителю работ\",\"Производителю работ (наблюдающему) и допускающему\"]', '', 2, ''),
(211, 186197, 'Что является определением термина \"эксплуатация\"?', '[\"Стадия жизненного цикла изделия, на которой реализуется, поддерживается или восстанавливается его качество\",\"Комплекс мероприятий, включающий в себя техническое обслуживание инженерных систем и коммуникаций\",\"Поддержание жизненного цикла изделия с целью его соответствия установленным требованиям технической документации\"]', '', 1, ''),
(212, 186198, 'У каких Потребителей электрической энергии должно быть организовано оперативное диспетчерское управление электрооборудованием?', '[\"У Потребителей, имеющих собственные источники электрической энергии\",\"У Потребителей, имеющих электроустановки напряжением свыше 1000 В\",\"У всех Потребителей, независимо от вида используемого электрооборудования\"]', '', 1, ''),
(213, 186199, 'Когда проводятся внеочередные осмотры воздушной линии электропередачи?', '[\"Только при образовании на проводах и тросах гололеда, после сильных бурь, ураганов и других стихийных бедствий, во время ледохода и разлива рек\",\"Только при пляске проводов\",\"Только при пожарах в зоне трассы ВЛ\",\"Только после отключения ВЛ релейной защитой и неуспешного автоматического повторного включения\",\"В любом из перечисленных случаев\"]', '', 5, ''),
(214, 186200, 'К какой категории, согласно Правилам устройства электроустановок, относятся электроприемники, перерыв электроснабжения которых может повлечь за собой опасность для жизни людей, угрозу для безопасности государства, значительный материальный ущерб, расстройство сложного технологического процесса, нарушение функционирования особо важных элементов коммунального хозяйства, объектов связи и телевидения?', '[\"К первой категории\",\"К особой группе первой категории\",\"Ко второй категории\",\"К третьей категории\"]', '', 1, ''),
(215, 186201, 'Допускается ли совмещенная прокладка токопроводов и технологических трубопроводов на общих опорах?', '[\"Допускается\",\"Не допускается\",\"Допускается, но только в горных районах\"]', '', 2, ''),
(216, 186202, 'Перечень состояний при которых не оказывается первая помощь в соответствии с приказом Минздрава России от 04.05.2012 N 477н?', '[\"Отсутствие сознания, остановка дыхания и кровообращения\",\"Наружные кровотечения, травмы различных областей тела\",\"Степень сильного алкогольного опьянения, нарушение координации\"]', '', 3, ''),
(217, 186203, 'С какой периодичностью каждый работник из числа диспетчерского, оперативного и оперативно-ремонтного персонала должен быть проверен в контрольной противопожарной тренировке?', '[\"1 раз в 6 календарных месяцев\",\"На усмотрение главного технического руководителя в зависимости от характера выполняемой работы работника\",\"1 раз в 3 календарных месяца\"]', '', 1, ''),
(218, 186204, 'Какое количество противоаварийных тренировок необходимо за время дублирования?', '[\"Не менее 10 противоаварийных тренировок\",\"Определяются индивидуальной программой подготовки по новой должности в соответствии с Правилами проведения противоаварийных тренировок\",\"Не менее 5 противоаварийных тренировок\",\"Не менее 7 противоаварийных тренировок\"]', '', 2, ''),
(219, 186205, 'Какую группу по электробезопасности должен иметь допускающий в электроустановках напряжением до 1000 В?', '[\"Группу IV по электробезопасности\",\"Группу III по электробезопасности\",\"Группу IV или V по электробезопасности\"]', '', 2, ''),
(220, 186206, 'Какие запрещающие плакаты вывешиваются на приводах коммутационных аппаратов во избежание подачи напряжения на рабочее место при проведении ремонта или планового осмотра оборудования?', '[\"\\\"Не включать! Работают люди\\\"\",\"\\\"Не открывать! Работают люди\\\"\",\"\\\"Работа под напряжением! Повторно не включать!\\\"\"]', '', 1, ''),
(221, 186207, 'Что является определением термина \"инструктаж целевой\"?', '[\"Указания по безопасному выполнению конкретной работы в электроустановке, охватывающие категорию работников, определенных нарядом или распоряжением, от выдавшего наряд, отдавшего распоряжение до члена бригады или исполнителя\",\"Указания по безопасному выполнению конкретной работы в электроустановке для членов бригады или исполнителей\",\"Указания по безопасному выполнению разовых работ, не связанных с прямыми должностными обязанностями по специальности\"]', '', 1, ''),
(222, 186208, 'Что находится в оперативном управлении старшего работника из числа оперативного персонала?', '[\"Оборудование и ЛЭП, токопроводы\",\"Устройства релейной защиты, аппаратура системы противоаварийной и режимной автоматики\",\"Средства диспетчерского и технологического управления\",\"Все перечисленные устройства и оборудование, операции с которыми требуют координации действий подчиненного оперативного персонала и согласованных изменений режимов на нескольких объектах\"]', '', 4, ''),
(223, 186209, 'Какие данные должны быть указаны на бирках кабелей в начале и конце линии?', '[\"Только марка и напряжение\",\"Только сечение кабеля и напряжение\",\"Только номер или наименование линии\",\"Марка, напряжение, сечение, номер или наименование линии\"]', '', 4, ''),
(224, 186210, 'К какой категории, согласно Правилам устройства электроустановок, относятся электроприемники, бесперебойная работа которых необходима для безаварийного останова производства с целью предотвращения угрозы жизни людей, взрывов и пожаров?', '[\"К первой категории\",\"К особой группе первой категории\",\"Ко второй категории\",\"К третьей категории\"]', '', 2, ''),
(225, 186211, 'На каком расстоянии на кабелях, проложенных в кабельных сооружениях, должны располагаться бирки?', '[\"Не реже чем через каждые 50 м\",\"Не реже чем через каждые 100 м\",\"Не реже чем через каждые 150 м\"]', '', 1, ''),
(226, 186212, 'Перечислите мероприятия по проведению сердечно-легочной реанимации до появления признаков жизни. (приказ Минздрава России от 04.05.2012 N 477н).', '[\"1) Давление руками на грудину пострадавшего;\\n2) искусственное дыхание «Рот ко рту» (»Рот к носу», с использованием устройства для искусственного дыхания)\",\"1) Освободить грудную клетку от одежды и расстегнуть поясной ремень;\\n2) прикрыть двумя пальцами мечевидный отросток;\\n3) нанести удар кулаком по грудине;\\n4) проверить пульс, при отсутствии пульса перейти к непрямому массажу сердца\",\"1) Убедиться в отсутствии пульса на сонной артерии;\\n2) прикрыть двумя пальцами мечевидный отросток, нанести удар по грудине;\\n3) перейти к непрямому массажу сердца;\\n4) сделать вдох искусственного дыхания\"]', '', 1, ''),
(227, 186213, 'Какие действия должны предприниматься в отношении работников, получивших неудовлетворительную оценку действий при проведении тренировки (противоаварийной или противопожарной)?', '[\"Повторная тренировка в сроки, определяемые уполномоченным должностным лицом организации, но не позднее 1 месяца\",\"Проведение специальной подготовки по выполнению противоаварийных (противопожарных) тренировок, приближенных к производственным\",\"Проведение целевого инструктажа\"]', '', 1, ''),
(228, 186214, 'Какой срок действия допуска к самостоятельной работе категорий диспетчерского, оперативного, оперативно-ремонтного и ремонтного персонала?', '[\"До очередной проверки знаний\",\"На 1 год\",\"На 6 месяцев\",\"На 2 года\"]', '', 1, '');
INSERT INTO `l4` (`id`, `globalId`, `question`, `answers`, `pravila`, `correctAnswer`, `glava`) VALUES
(229, 186215, 'За выполнение какой из перечисленных функций не несет ответственность производитель работ?', '[\"За соответствие подготовленного рабочего места мероприятиям, необходимым при подготовке рабочих мест и отдельным указаниям наряда-допуска, за четкость и полноту целевого инструктажа членов бригады\",\"За наличие, исправность и правильное применение необходимых средств защиты, инструмента, инвентаря и приспособлений\",\"За безопасное проведение работы и соблюдение Правил им самим и членами бригады и за осуществление постоянного контроля за членами бригады\",\"За достаточность и правильность указанных в наряде-допуске (распоряжении) мер безопасности, за качественный и количественный состав бригады\"]', '', 4, ''),
(230, 186216, 'Кто имеет право проводить обслуживание аккумуляторных батарей и зарядных устройств?', '[\"Специально обученный персонал, имеющий II группу по электробезопасности\",\"Любой работник из числа электротехнического персонала, имеющий III группу по электробезопасности\",\"Специально обученный персонал, имеющий III группу по электробезопасности\"]', '', 3, ''),
(231, 186217, 'Что является определением термина \"глухозаземленная нейтраль\"?', '[\"Нейтраль трансформатора или генератора, не присоединенная к заземляющему устройству или присоединенная к нему через большое сопротивление приборов сигнализации, измерения, защиты и других аналогичных им устройств\",\"Нейтраль трансформатора или генератора, присоединенная к заземляющему устройству непосредственно\",\"Нейтраль трансформатора или генератора, присоединенная к заземляющему устройству через активные токоограничивающие сопротивления\"]', '', 2, ''),
(232, 186218, 'Что находится в оперативном ведении старшего работника из числа оперативного персонала?', '[\"Оборудование и ЛЭП, токопроводы\",\"Устройства релейной защиты, аппаратура системы противоаварийной и режимной автоматики\",\"Средства диспетчерского и технологического управления\",\"Все перечисленные устройства и оборудование, операции с которыми не требуют координации действий персонала разных энергетических объектов\"]', '', 4, ''),
(233, 186219, 'Как часто должны проводиться осмотры кабельных колодцев линий напряжением до 35 кВ?', '[\"Не реже одного раза в три месяца\",\"Не реже одного раза в 6 месяцев\",\"Не реже одного раза в год\",\"Не реже одного раза в два года\"]', '', 4, ''),
(234, 186220, 'К какой категории, согласно Правилам устройства электроустановок, относятся электроприемники, перерыв электроснабжения которых приводит к массовому недоотпуску продукции, массовым простоям рабочих, механизмов и промышленного транспорта, нарушению нормальной деятельности значительного количества городских и сельских жителей?', '[\"К первой категории\",\"К особой группе первой категории\",\"Ко второй категории\",\"К третьей категории\"]', '', 3, ''),
(235, 186221, 'Какое максимальное количество силовых кабелей, при прокладке в земле, рекомендуется прокладывать в траншее?', '[\"6 силовых кабелей\",\"8 силовых кабелей\",\"10 силовых кабелей\"]', '', 1, ''),
(236, 186222, 'Какие предпринимаются действия по поддержанию проходимости дыхательных путей? (приказ Минздрава России от 04.05.2012 N 477н).', '[\"1) До приезда скорой помощи периодически делать «вдох» искусственного дыхания;\\n2) приложить холод к голове\",\"1) Придать пострадавшему устойчивое боковое положение;\\n2) запрокинуть голову с подъемом подбородка;\\n3) выдвинуть нижнюю челюсть\",\"1) Положить пострадавшего на живот;\\n2) подогнуть колени;\\n3) вызвать рвотные позывы\"]', '', 2, ''),
(237, 186223, 'На какой персонал распространяются требования специальной подготовки?', '[\"На работников из числа диспетчерского, оперативного и оперативно-ремонтного персонала\",\"На управленческий персонал и специалистов производственных подразделений\",\"На ремонтный персонал, связанный с техническим обслуживанием, ремонтом, наладкой и испытанием энергоустановок\"]', '', 1, ''),
(238, 186224, 'В каких случаях может быть отозван допуск к самостоятельной работе?', '[\"Неудовлетворительной оценки, полученной работником по результатам проверки знаний\",\"Заключений (актов) комиссий, расследовавших несчастные случаи, аварии в электроэнергетике, пожары, инциденты\",\"Неудовлетворительной оценки, полученной работником повторно на индивидуальной противоаварийной или противопожарной тренировке\",\"Во всех перечисленных случаях\"]', '', 4, ''),
(239, 186225, 'В каком из перечисленных случаев производитель работ должен иметь IV группу по электробезопасности?', '[\"При выполнении работ по наряду-допуску в электроустановках напряжением выше 1000 В\",\"При выполнении работ в подземных сооружениях, где возможно появление вредных газов\",\"При выполнении работ по перетяжке и замене проводов на ВЛ напряжением до 1000 В, подвешенных на опорах ВЛ напряжением выше 1000 В\",\"В любом из перечисленных случаев\"]', '', 4, ''),
(240, 186226, 'Каким образом должна быть обеспечена защита от потенциала при работах на проводах, выполняемых с телескопической вышки?', '[\"Рабочая площадка вышки должна быть с помощью специальной штанги соединена с проводом линии гибким медным проводником сечением не менее 10 мм2, а сама вышка заземлена\",\"Рабочая площадка вышки должна быть с помощью специальной штанги соединена с проводом линии гибким медным проводником сечением не менее 12 мм2, а сама вышка заземлена\",\"Рабочая площадка вышки должна быть с помощью специальной штанги соединена с проводом линии гибким медным проводником сечением не менее 16 мм2, а сама вышка заземлена\",\"Рабочая площадка вышки должна быть с помощью специальной штанги соединена с проводом линии гибким медным проводником сечением не менее 20 мм2, а сама вышка заземлена\"]', '', 1, ''),
(241, 186227, 'Что является определением термина \"силовая электрическая цепь\"?', '[\"Электрическая цепь, содержащая элементы, функциональное назначение которых состоит в производстве или передаче основной части электрической энергии, ее распределении, преобразовании в другой вид энергии или в электрическую энергию с другими значениями параметров\",\"Устройство, выполненное в виде шин или проводов с изоляторами и поддерживающими конструкциями, предназначенное для передачи и распределения электрической энергии в пределах электростанции, подстанции или цеха\",\"Комплект элементов, связывающих присоединения электрического распределительного устройства\",\"Электрическая сеть переменного или постоянного тока, предназначенная для передачи и распределения электрической энергии, используемой в цепях управления, автоматики, защиты и сигнализации электростанции (подстанции)\"]', '', 1, ''),
(242, 186228, 'Сколько человек из числа оперативного персонала должны выполнять сложные переключения на электроустановках?', '[\"Один\",\"Два, причем один является контролирующим\",\"Три, один из которых является контролирующим\",\"Любое количество человек\"]', '', 2, ''),
(243, 186229, 'Кто периодически должен проводить выборочный осмотр кабельных линий?', '[\"Оперативный персонал Потребителя\",\"Административно-технический персонал Потребителя\",\"Оперативно-ремонтный персонал Потребителя\",\"Представители управления энергонадзора территориального органа Ростехнадзора\"]', '', 2, ''),
(244, 186230, 'Какое минимальное количество независимых взаимно резервирующих источников питания, согласно Правилам устройства электроустановок, должно обеспечивать электроэнергией электроприемники первой категории в нормальных режимах, если перерыв их электроснабжения при нарушении электроснабжения от одного из источников питания может быть допущен лишь на время автоматического восстановления питания?', '[\"Два источника питания\",\"Три источника питания\",\"Четыре источника питания\",\"Шесть источников питания\"]', '', 1, ''),
(245, 186231, 'Каким должно быть минимальное расстояние в свету от кабеля, проложенного непосредственно в земле, до фундаментов зданий и сооружений?', '[\"0,4 м\",\"0,5 м\",\"0,6 м\"]', '', 3, ''),
(246, 186232, 'Перечислите мероприятия по обзорному осмотру пострадавшего и временной остановке наружного кровотечения. (приказ Минздрава России от 04.05.2012 N 477н).', '[\"1) Прижать к ране сложенную чистую ткань или бинт;\\n2) уложить пострадавшего в горизонтальное положение;\\n3) использовать пальцевое прижатие раны;\\n4) фиксировать повязку косынкой, платком или шапкой-ушанкой\",\"1) Осмотр пострадавшего на наличие кровотечений;\\n2) проведение осмотра конечностей;\\n3) наложение повязок при травмах различных областей тела;\\n4) придание пострадавшему оптимального положения тела\",\"1) Обзорный осмотр пострадавшего на наличие кровотечений;\\n2) пальцевое прижатие артерии;\\n3) наложение жгута;\\n4) максимальное сгибание конечности в суставе;\\n5) прямое давление на рану;\\n6) наложение давящей повязки\"]', '', 3, ''),
(247, 186233, 'С какой периодичностью должно проводиться длительное периодическое обучение работников, относящихся к категориям административно-технического, диспетчерского, оперативного, оперативно-ремонтного и ремонтного персонала?', '[\"Не реже 1 раза в 5 лет\",\"По мере необходимости, но не реже 1 раза в 3 года\",\"В зависимости от образования и стажа работника по решению руководителя организации, а для самих руководителей по решению вышестоящей организации\"]', '', 1, ''),
(248, 186234, 'Какие ознакомительные мероприятия проводятся перед допуском к самостоятельной работе персонала, имевшего перерыв в работе, независимо от проводимых форм подготовки в соответствии с занимаемой должностью?', '[\"Ознакомлен с изменениями в оборудовании, схемах и режимах работы энергоустановок\",\"Ознакомлен с введенными в действие (вступившими в силу) новыми отраслевыми актами и инструктивно-техническими документами в сфере электроэнергетики (оперативный, оперативно-ремонтный и ремонтный персонал- также в сфере теплоснабжения), являющимися обязательными для использования в работе и исполнения согласно должностным обязанностям (трудовым функциям) работника\",\"Ознакомлен с изменениями, внесенными в действующие отраслевые акты и инструктивно-технические документы в сфере электроэнергетики (оперативный, оперативно-ремонтный и ремонтный персонал, - также в сфере теплоснабжения), являющиеся обязательными для использования в работе и исполнения согласно должностным обязанностям (трудовым функциям) работника\",\"Все перечисленное\"]', '', 4, ''),
(249, 186235, 'Какие из перечисленных функций не входят в обязанности наблюдающего?', '[\"Отвечает за качественный и количественный состав бригады\",\"Отвечает за четкость и полноту целевого инструктажа членов бригады\",\"Отвечает за наличие и сохранность установленных на рабочем месте заземлений, ограждений, плакатов и знаков безопасности, запирающих устройств приводов\",\"Отвечает за безопасность членов бригады в отношении поражения электрическим током электроустановки\"]', '', 1, ''),
(250, 186236, 'Как классифицируются электроинструмент и ручные электрические машины по способу защиты от поражения электрическим током?', '[\"Делятся на 4 класса - нулевой, первый, второй и третий\",\"Делятся на 3 класса - первый, второй и третий\",\"Делятся на 4 класса - первый, второй, третий и четвертый\",\"Делятся на 3 класса - нулевой, первый и второй\"]', '', 1, ''),
(251, 186237, 'Что является определением термина \"токопровод\"?', '[\"Устройство, выполненное в виде шин или проводов с изоляторами и поддерживающими конструкциями, предназначенное для передачи и распределения электрической энергии в пределах электростанции, подстанции или цеха\",\"Комплект элементов, связывающих присоединения электрического распределительного устройства\",\"Электрическая сеть переменного или постоянного тока, предназначенная для передачи и распределения электрической энергии, используемой в цепях управления, автоматики, защиты и сигнализации электростанции (подстанции)\",\"Электрическая цепь, содержащая элементы, функциональное назначение которых состоит в производстве или передаче основной части электрической энергии, ее распределении, преобразовании в другой вид энергии или в электрическую энергию с другими значениями параметров\"]', '', 1, ''),
(252, 186238, 'Кто утверждает список работников, имеющих право выполнять оперативные переключения?', '[\"Ответственный за электрохозяйство Потребителя\",\"Главный энергетик Потребителя\",\"Руководитель Потребителя\",\"Никто не утверждает\"]', '', 3, ''),
(253, 186239, 'Кем должен быть утвержден перечень ответственных механизмов, участвующих в самозапуске?', '[\"Руководителем Потребителя\",\"Техническим руководителем Потребителя\",\"Руководителем подразделения Потребителя, эксплуатирующего оборудование\",\"Руководителем подразделения Потребителя, обслуживающего оборудование\"]', '', 2, ''),
(254, 186240, 'Какое минимальное количество независимых, взаимно резервирующих источников питания, согласно Правилам устройства электроустановок, должно обеспечивать электроэнергией электроприемники особой группы первой категории в нормальных режимах, если перерыв их электроснабжения при нарушении электроснабжения от одного из источников питания может быть допущен лишь на время автоматического восстановления питания?', '[\"Два источника питания\",\"Три источника питания\",\"Четыре источника питания\",\"Шесть источников питания\"]', '', 2, ''),
(255, 186241, 'Каким должно быть расстояние в свету между кабелем и стенкой канала теплопровода при прокладке кабельной линии параллельно с теплопроводом?', '[\"Не менее 0,5 м\",\"Не менее 1,0 м\",\"Не менее 2,0 м\"]', '', 3, ''),
(256, 186242, 'Какие действия оказывающего помощь не относятся к мероприятиям по подробному осмотру пострадавшего в целях выявления признаков травм, отравлений и других состояний, угрожающих его жизни и здоровью, и по оказанию первой помощи в случае выявления указанных состояний (приказ Минздрава России от 04.05.2012 N 477н)?', '[\"1) Осмотр пострадавшего на наличие кровотечений;\\n2) прекращение воздействия опасных химических веществ на пострадавшего проведением клизмы;\\n3) наложение гипсовых повязок при повреждении конечностей;\\n4) охлаждение поврежденных конечностей в течение получаса\",\"1) Проведение осмотра головы;\\n2) проведение осмотра шеи;\\n3) проведение осмотра груди;\\n4) проведение осмотра спины;\\n5) проведение осмотра живота и таза;\\n6) проведение осмотра конечностей;\\n7) наложение повязок при травмах различных областей тела, в том числе окклюзионной (герметизирующей) при ранении грудной клетки\",\"1) Проведение иммобилизации;\\n2) прекращение воздействия опасных химических веществ на пострадавшего (промывание желудка путем приема воды и вызывания рвоты, удаление с поврежденной поверхности и промывание поврежденной поверхности проточной водой);\\n3) местное охлаждение при травмах, термических ожогах и иных воздействиях высоких температур или теплового излучения;\\n4) термоизоляция при отморожениях и других эффектах воздействия низких температур\"]', '', 1, ''),
(257, 186243, 'Укажите, как часто должны осуществляться обходы и осмотры рабочих мест уполномоченными лицами организации?', '[\"Периодичность, порядок их организации и проведения определяет руководитель организации или уполномоченное им должностное лицо\",\"Не менее раза в месяц\",\"Не менее 1 раза в 10 дней\"]', '', 1, ''),
(258, 186244, 'Для какой категории персонала производственный инструктаж является обязательным?', '[\"Диспетчерского, оперативного, оперативно-ремонтного и ремонтного персонала\",\"Административно-технического персонала\",\"Только для технического персонала\",\"Для всех\"]', '', 1, ''),
(259, 186245, 'Допускается ли в состав бригады, выполняющей работы по наряд-допуску, включать работников, имеющих II группу по электробезопасности?', '[\"Не допускается\",\"На каждого работника, имеющего группу III, допускается включать двух работников, имеющих группу II\",\"На каждого работника, имеющего группу III, допускается включать одного работника, имеющего группу II, но не более трех в бригаду\"]', '', 3, ''),
(260, 186246, 'Электроинструмент какого класса можно применять без использования электрозащитных средств при производстве работ в металлических емкостях с ограниченной возможностью перемещения и выхода?', '[\"Класса I\",\"Класса II\",\"Класса III\"]', '', 3, ''),
(261, 186247, 'Чем должны быть укомплектованы электроустановки?', '[\"Защитными средствами, средствами пожаротушения\",\"Средствами пожаротушения, исправным инструментом и средствами оказания первой медицинской помощи\",\"Исправным инструментом\",\"Испытанными защитными средствами, средствами пожаротушения, исправным инструментом и средствами оказания первой помощи\"]', '', 4, ''),
(262, 186248, 'Кто может выводить из работы блокировки оборудования и устройств релейной защиты и автоматики?', '[\"Оперативный персонал, непосредственно выполняющий переключения\",\"Оперативно-ремонтный персонал, имеющий группу по электробезопасности не ниже IV\",\"Ремонтный персонал участка релейной защиты и автоматики, имеющий группу по электробезопасности не ниже V\",\"Работники, уполномоченные на это письменным распоряжением ответственного за электрохозяйство Потребителя\"]', '', 4, ''),
(263, 186249, 'Какое из положений не соответствует Правилам технической эксплуатации электроустановок потребителей при эксплуатации электродвигателей?', '[\"Электродвигатели с водяным охлаждением активной стали статора и обмотки ротора должны быть оборудованы устройствами, сигнализирующими о появлении воды в корпусе\",\"На групповых сборках и щитках электродвигателей должны быть предусмотрены вольтметры или сигнальные лампы контроля наличия напряжения\",\"На электродвигателях, имеющих принудительную смазку подшипников, должна быть установлена защита, действующая только на сигнал\",\"При изменении частоты питающей сети в пределах 2,5 % от номинального значения допускается работа электродвигателей с номинальной мощностью\"]', '', 3, ''),
(264, 186250, 'Какое минимальное количество источников питания, согласно Правилам устройства электроустановок, должно обеспечивать электроэнергией электроприемники третьей категории в нормальных режимах при условии, что перерывы электроснабжения, необходимые для ремонта или замены поврежденного элемента системы электроснабжения, не превышают 1 суток?', '[\"Один источник питания\",\"Два источника питания\",\"Три источника питания\",\"Четыре источника питания\"]', '', 1, ''),
(265, 186251, 'Когда допускается переход кабелей из блоков в землю без кабельных колодцев?', '[\"При числе кабелей до 10 и напряжении не выше 35 кВ\",\"При числе кабелей более 10 и напряжении выше 35 кВ\",\"Не допускается без кабельных колодцев осуществлять переход кабелей из блоков в землю\"]', '', 1, ''),
(266, 186252, 'На каком этапе производится вызов скорой медицинской помощи, других специальных служб, сотрудники которых обязаны оказывать первую помощь в соответствии с федеральным законом или со специальным правилом согласно приказу Минздрава России от 04.05.2012 N 477н?', '[\"По окончании проведения сердечно-легочной реанимации и при появлении признаков жизни\",\"После обнаружения пострадавшего и оценки обстановки по обеспечению безопасных условий для оказания первой помощи\",\"После осмотра пострадавшего и временной остановки наружного кровотечения\"]', '', 2, ''),
(267, 186253, 'Как учитывается время, затраченное на проведение противоаварийных и противопожарных тренировок в соответствии с требованиями \"Правил работы с персоналом в организациях электроэнергетики Российской Федерации\"?', '[\"Включается в рабочее время тренирующихся\",\"Учитывается и оплачивается как работа в выходной или не рабочий праздничный день\"]', '', 1, ''),
(268, 186254, 'Какие вопросы включает программа планового производственного инструктажа?', '[\"Особенности и режимы эксплуатации оборудования в период его пуска, нормальной работы, останова, консервации\",\"Принципы работы оборудования и устройств (комплексов) РЗА\",\"Вопросы управления электроэнергетическим режимом и иные технологические вопросы\",\"Все перечисленное\"]', '', 4, ''),
(269, 186255, 'Сколько работников, имеющих II группу по электробезопасности, допускается включать в бригаду?', '[\"По одному на каждого работника, имеющего III группу по электробезопасности\",\"Общее число членов бригады, имеющих II группу, не должно превышать трех человек\",\"Численность работников определяется производителем работ\",\"Численность работников определяется исходя из условий выполнения работ\"]', '', 2, ''),
(270, 186256, 'Каковы условия применения электроинструмента класса II в особо опасных помещениях?', '[\"Без применения электрозащитных средств при подключении через устройство защитного отключения\",\"Без применения электрозащитных средств\",\"С применением хотя бы одного электрозащитного средства\",\"Не допускается применять\"]', '', 2, ''),
(271, 186257, 'У каких Потребителей можно не назначать ответственного за электрохозяйство?', '[\"У Потребителей, не занимающихся производственной деятельностью, электрохозяйство которых включает в себя только вводное (вводно-распределительное) устройство, осветительные установки, переносное электрооборудование номинальным напряжением не выше 380 В\",\"У Потребителей, занимающихся производственной деятельностью, электрохозяйство которых включает в себя только вводное (вводно-распределительное) устройство, осветительные установки, переносное электрооборудование номинальным напряжением не выше 660 В\",\"У Потребителей, установленная мощность электроустановок которых превышает 10 кВА\"]', '', 1, ''),
(272, 186258, 'Какие требования безопасности должен выполнять оперативный персонал при исчезновении напряжения на электроустановке?', '[\"Должен быть готов к появлению напряжения без предупреждения в любое время\",\"Должен действовать согласно должностной инструкции\",\"Должен действовать согласно ПЛА\",\"Должен выполнять последовательность операций, определяемую вышестоящим оперативным персоналом\"]', '', 1, ''),
(273, 186259, 'Что из перечисленного должен осуществлять оперативный персонал?', '[\"Только контроль правильности положения переключающих устройств на панелях (шкафах) релейной защиты, автоматики и телемеханики и управления\",\"Только опробование высоковольтных выключателей, устройств автоматического повторного включения и автоматического включения резерва\",\"Только измерение тока небаланса в защите шин\",\"Оперативный персонал должен осуществлять все перечисленное\"]', '', 4, ''),
(274, 186260, 'Что представляет собой система ТN для электроустановок напряжением до 1 кВ?', '[\"Система, в которой нулевой защитный и нулевой рабочий проводники совмещены в одном проводнике на всем ее протяжении\",\"Система, в которой нейтраль источника питания глухо заземлена, а открытые проводящие части электроустановки присоединены к глухозаземленной нейтрали источника посредством нулевых защитных проводников\",\"Система, в которой нейтраль источника питания изолирована от земли или заземлена через приборы или устройства, имеющие большое сопротивление, а открытые проводящие части электроустановки заземлены\"]', '', 2, ''),
(275, 186261, 'Допускается ли в кабельном сооружении иметь один выход?', '[\"Допускается\",\"Не допускается\",\"Допускается, но только при длине кабельного сооружения не более 25 м\",\"Допускается, но только при длине кабельного сооружения не менее 50 м\"]', '', 3, ''),
(276, 186262, 'Укажите верный перечень исчерпывающих мероприятий по оказанию первой помощи (приказ Минздрава России от 04.05.2012 N 477н).', '[\"1) Определение угрожающих факторов для жизни и здоровья пострадавшего;\\n2) устранение угрожающих факторов для жизни и здоровья;\\n3) вызов скорой медицинской помощи;\\n4) прекращение действия повреждающих факторов на пострадавшего;\\n5) оценка количества пострадавших\",\"1) Определение угрожающих факторов для собственной жизни и здоровья;\\n2) определение угрожающих факторов для жизни и здоровья пострадавшего;\\n3) устранение угрожающих факторов для жизни и здоровья;\\n4) прекращение действия повреждающих факторов на пострадавшего;\\n5) оценка количества пострадавших;\\n6) извлечение пострадавшего из транспортного средства или других труднодоступных мест;\\n7) перемещение пострадавшего\",\"1) Вызов скорой медицинской помощи;\\n2) других специальных служб, сотрудники которых обязаны оказывать первую помощь в соответствии с федеральным законом или со специальным правилом;\\n3) устранение угрожающих факторов для жизни и здоровья;\\n4) прекращение действия повреждающих факторов на пострадавшего;\\n5) оценка количества пострадавших;\\n6) извлечение пострадавшего из транспортного средства или других труднодоступных мест\"]', '', 2, ''),
(277, 186263, 'В какие сроки должны проводиться учебные и контрольные противоаварийные тренировки для работников, относящихся к категории диспетчерского, оперативного, оперативно-ремонтного персонала?', '[\"В порядке, установленном Правилами проведения противоаварийных тренировок\",\"Один раз в квартал\",\"Один раз в год\"]', '', 1, ''),
(278, 186264, 'С какой периодичностью должны проводиться плановые производственные инструктажи для диспетчерского, оперативного и оперативно-ремонтного персонала?', '[\"1 раз в месяц\",\"1 раз в 3 месяца\",\"1 раз в 6 месяцев\",\"1 раз в 12 месяцев\"]', '', 1, ''),
(279, 186265, 'В каких случаях оперативный персонал, находящийся на дежурстве, можно привлекать к работе в бригаде по наряд-допуску?', '[\"Можно, по разрешению работника из числа вышестоящего оперативного персонала и оформлением в наряд-допуске\",\"Можно, по разрешению работника из числа вышестоящего оперативного персонала, с записью в оперативном журнале и оформлением в наряд-допуске\",\"Ни в каких случаях\",\"Можно, по разрешению работника из числа вышестоящего оперативного персонала\"]', '', 2, ''),
(280, 186266, 'Что запрещено работнику при выполнении работ с применением переносного электроинструмента?', '[\"Выполнять тестирование устройства защитного отключения\",\"Проверять комплектность и надежность крепления деталей\",\"Проверять исправность цепи заземления у машин I класса\",\"Разбирать ручные электрические машины и электроинструмент, производить какой-либо ремонт\"]', '', 4, ''),
(281, 186267, 'Что из перечисленного входит в обязанности ответственного за электрохозяйство?', '[\"Укомплектование электроустановок защитными средствами, средствами пожаротушения и инструментом\",\"Подбор электротехнического и электротехнологического персонала\",\"Организация обучения, инструктирования, проверки знаний и допуска к самостоятельной работе электротехнического персонала\",\"Все перечисленное здесь входит в обязанности ответственного за электрохозяйство\"]', '', 3, ''),
(282, 186268, 'На кого возложена обязанность по составлению годовых планов (графиков) по ремонту основного оборудования электроустановок?', '[\"На технического руководителя организации\",\"На ответственного за электрохозяйство\",\"На оперативный персонал Потребителя\",\"На административно-технический персонал Потребителя\"]', '', 2, ''),
(283, 186269, 'Каким образом производится присоединение заземляющих проводников к заземлителю и заземляющим конструкциям?', '[\"Сваркой\",\"Болтовым соединением\",\"Резьбовым соединением\",\"Фланцевым соединением\"]', '', 1, ''),
(284, 186270, 'Что представляет собой система TN-C для электроустановок напряжением до 1 кВ?', '[\"Система, в которой нулевой защитный и нулевой рабочий проводники совмещены в одном проводнике на всем ее протяжении\",\"Система, в которой нейтраль источника питания глухо заземлена, а открытые проводящие части электроустановки присоединены к глухозаземленной нейтрали источника посредством нулевых защитных проводников\",\"Система, в которой нейтраль источника питания изолирована от земли, либо заземлена через приборы или устройства, имеющие большое сопротивление, а открытые проводящие части электроустановки заземлены\",\"Система, в которой нулевой защитный и нулевой рабочий проводники разделены на всем ее протяжении\"]', '', 1, ''),
(285, 186271, 'Чем должны перекрываться кабельные каналы и двойные полы в распределительных устройствах и помещениях?', '[\"Съемными несгораемыми плитами\",\"Рифленой сталью\",\"Деревянными щитами с паркетом, защищенными снизу асбестом и по асбесту жестью\",\"Пластиковыми перекрытиями\"]', '', 1, ''),
(286, 186272, 'Какие из перечисленных изолирующих электрозащитных средств относятся к основным изолирующим электрозащитным средствам для электроустановок напряжением до 1000 В?', '[\"Изолирующие клещи\",\"Диэлектрические галоши\",\"Диэлектрические ковры и изолирующие подставки\",\"Изолирующие колпаки, покрытия и накладки\"]', '', 1, ''),
(287, 186273, 'Допускается ли совмещение контрольных противоаварийных тренировок и контрольных противопожарных тренировок в соответствии с требованиями \"Правил работы с персоналом в организациях электроэнергетики Российской Федерации\"?', '[\"Да\",\"Нет\",\"По согласованию с МЧС\"]', '', 1, ''),
(288, 186274, 'С какой периодичностью должны проводиться плановые производственные инструктажи для ремонтного персонала?', '[\"1 раз в 3 месяца\",\"1 раз в 12 месяцев\",\"1 раз в 6 месяцев\",\"1 раз в 4 месяца\"]', '', 1, ''),
(289, 186275, 'Какие дополнительные обязанности может выполнять выдающий наряд-допуск, отдающий распоряжение?', '[\"Ответственный руководитель работ, производитель работ, допускающий (в электроустановках, не имеющих местного оперативного персонала)\",\"Производителя работ, допускающего\",\"Допускающего\"]', '', 1, ''),
(290, 186276, 'Какие требования предъявляются к командированному персоналу?', '[\"Командируемый персонал должен иметь профессиональную подготовку\",\"Командируемый персонал должен иметь удостоверения о проверке знаний правил работы в электроустановках с отметкой о группе по электробезопасности\",\"Командируемый персонал должен быть обучен и аттестован по охране труда и промышленной безопасности, если это необходимо\",\"Командируемый персонал должен пройти предварительное медицинское обследование\"]', '', 2, ''),
(291, 186277, 'Что из перечисленного не входит в обязанности ответственного за электрохозяйство?', '[\"Контроль наличия, своевременности проверок и испытаний средств защиты в электроустановках, средств пожаротушения и инструмента\",\"Организация проведения расчетов потребности Потребителя в электрической энергии и осуществление контроля за ее расходованием\",\"Непосредственное обслуживание электроустановок\",\"Организация разработки и ведения необходимой документации по вопросам организации эксплуатации электроустановок\"]', '', 3, ''),
(292, 186278, 'На какие виды ремонтов основного оборудования электроустановок должны составляться годовые планы (графики)?', '[\"На текущие ремонты\",\"На капитальные ремонты\",\"На планово-предупредительные ремонты\",\"На все виды ремонтов\"]', '', 4, ''),
(293, 186279, 'В какой цвет должны быть окрашены открыто проложенные заземляющие проводники?', '[\"В синий цвет\",\"В зеленый цвет\",\"В черный цвет\",\"В красный цвет\"]', '', 3, ''),
(294, 186280, 'Что представляет собой система TN-S для электроустановок напряжением до 1 кВ?', '[\"Система, в которой нулевой защитный и нулевой рабочий проводники совмещены в одном проводнике на всем ее протяжении\",\"Система, в которой нейтраль источника питания глухо заземлена, а открытые проводящие части электроустановки присоединены к глухозаземленной нейтрали источника посредством нулевых защитных проводников\",\"Система, в которой нейтраль источника питания изолирована от земли, либо заземлена через приборы или устройства, имеющие большое сопротивление, а открытые проводящие части электроустановки заземлены\",\"Система, в которой нулевой защитный и нулевой рабочий проводники разделены на всем ее протяжении\"]', '', 4, ''),
(295, 186281, 'Каким должно быть расстояние в производственных помещениях между параллельно проложенными силовыми кабелями и трубопроводами с горючими жидкостями?', '[\"Не менее 0,25 м\",\"Не менее 0,5 м\",\"Не менее 0,8 м\",\"Не менее 1,0 м\"]', '', 4, ''),
(296, 186282, 'Какие из перечисленных изолирующих электрозащитных средств относятся к дополнительным изолирующим электрозащитным средствам для электроустановок напряжением до 1000 В?', '[\"Диэлектрические галоши\",\"Изолирующие штанги всех видов\",\"Изолирующие клещи\",\"Указатели напряжения\"]', '', 1, ''),
(297, 186283, 'Когда должна осуществляться подготовка персонала для вводимых в работу новых и реконструируемых объектов электроэнергетики?', '[\"За полгода до ввода в эксплуатацию\",\"До начала проведения пробных пусков и комплексного опробования оборудования\",\"За год до ввода в эксплуатацию\"]', '', 2, ''),
(298, 186284, 'В каких случаях проводится внеплановый производственный инструктаж?', '[\"При принятии новой или внесении изменений в действующую инструктивно-техническую документацию диспетчерских центров субъекта оперативно-диспетчерского управления, инструктивную документацию организации по вопросам\",\"При внесении изменений в действующие отраслевые акты в сфере электроэнергетики (для персонала объектов по производству электрической энергии, функционирующих в режиме комбинированной выработки электрической и тепловой энергии, - также в сфере теплоснабжения), являющиеся обязательными для использования в работе и исполнения согласно должностным обязанностям (трудовым функциям) работника\",\"По решению руководителя или иного уполномоченного должностного лица организации (ее филиала, представительства) при установлении нарушений работниками требований отраслевых актов и (или) инструктивно-технических документов\",\"В случае непрохождения работником планового производственного инструктажа (по темам пропущенного инструктажа\",\"Во всех перечисленных случаях\"]', '', 5, ''),
(299, 186285, 'Какие дополнительные обязанности может выполнять ответственный руководитель работ?', '[\"Ответственного руководителя работ, производителя работ, допускающего\",\"Допускающего\",\"Производитель работ, допускающий (в электроустановках, не имеющих местного оперативного персонала)\"]', '', 3, ''),
(300, 186286, 'Что должен пройти командированный персонал по прибытии на место своей командировки для выполнения работ в действующих электроустановках?', '[\"Индивидуальную теоретическую подготовку\",\"Контрольную противоаварийную тренировку\",\"Вводный и первичный инструктажи по охране труда\",\"Ознакомление с текущими распорядительными документами организации по вопросам аварийности и травматизма\"]', '', 3, ''),
(301, 186287, 'Какую периодичность повышения квалификации должен обеспечивать работодатель для персонала?', '[\"Не реже одного раза в год\",\"Не реже одного раза в три года\",\"Не реже одного раза в пять лет\",\"Не реже одного раза в десять лет\"]', '', 3, ''),
(302, 186288, 'Когда возникает необходимость проведения технического освидетельствования электрооборудования?', '[\"Техническое освидетельствование проводится с периодичностью не реже одного раза в 5 лет\",\"Техническое освидетельствование проводится по истечении установленного нормативно-технической документацией срока службы электрооборудования\",\"Необходимость в техническом освидетельствовании электрооборудования определяется в результате осмотра электрооборудования\"]', '', 2, ''),
(303, 186289, 'Какова периодичность осмотров заземляющих устройств с выборочным вскрытием грунта?', '[\"По графику, но не реже 1 раза в год\",\"По графику, но не реже 1 раза в 3 года\",\"По графику, но не реже 1 раза в 6 лет\",\"По графику, но не реже 1 раза в 9 лет\",\"По графику, но не реже 1 раза в 12 лет\"]', '', 5, ''),
(304, 186290, 'Что представляет собой система TN-C-S для электроустановок напряжением до 1 кВ?', '[\"Система, в которой нулевой защитный и нулевой рабочий проводники совмещены в одном проводнике на всем ее протяжении\",\"Система, в которой нейтраль источника питания глухо заземлена, а открытые проводящие части электроустановки присоединены к глухозаземленной нейтрали источника посредством нулевых защитных проводников\",\"Система, в которой функции нулевого защитного и нулевого рабочего проводников совмещены в одном проводнике в какой-то ее части, начиная от источника питания\",\"Система, в которой нулевой защитный и нулевой рабочий проводники разделены на всем ее протяжении\"]', '', 3, ''),
(305, 186291, 'Какой тип опор устанавливается в местах изменения направления трассы воздушной линии электропередачи?', '[\"Промежуточные опоры\",\"Анкерные опоры\",\"Угловые опоры\",\"Концевые опоры\"]', '', 3, ''),
(306, 186292, 'Что необходимо сделать при обнаружении непригодности средств защиты?', '[\"Сдать на внеочередной осмотр и испытания\",\"Поставить в известность руководителя организации\",\"Изъять из эксплуатации, сделать запись в журнале учета и содержания средств защиты об изъятии или в оперативной документации\",\"Внести изменения в Положение о продлении срока эксплуатации СИЗ\"]', '', 3, ''),
(307, 186293, 'На кого не распространяются требования \"Правил работы с персоналом в организациях электроэнергетики Российской Федерации\"?', '[\"Системного оператора и субъекты оперативно-диспетчерского управления в электроэнергетике в технологически изолированных территориальных электроэнергетических системах (далее - субъекты оперативно-диспетчерского управления)\",\"Субъекты электроэнергетики и потребителей электрической энергии, владеющих на праве собственности или ином законном основании объектами по производству электрической энергии, в том числе объектами, функционирующими в режиме комбинированной выработки электрической и тепловой энергии\",\"Субъекты электроэнергетики и потребителей электрической энергии, владеющих на праве собственности или ином законном основании объектами электросетевого хозяйства\",\"Потребителей электрической энергии и владельцев объектов электроэнергетики, являющихся физическими лицами\"]', '', 4, ''),
(308, 186295, 'Сколько экземпляров наряда-допуска должно оформляться?', '[\"Достаточно одного\",\"Наряд-допуск оформляется в двух экземплярах, а при передаче по телефону, радио, факсимильным или электронным письмом - в трех\",\"Наряд-допуск оформляется в трех экземплярах\"]', '', 2, ''),
(309, 186296, 'Кто проводит первичный инструктаж командированному персоналу при проведении работ в электроустановках до 1000 В?', '[\"Работник организации - владельца электроустановок из числа административно-технического персонала, имеющий группу IV\",\"Работник организации - владельца электроустановок из числа электротехнического персонала, имеющий группу IV\",\"Работник организации - владельца электроустановок из числа оперативно-ремонтного персонала, имеющий группу IV\",\"Работник командирующей организации из числа административно-технического персонала, имеющий группу IV\"]', '', 1, ''),
(310, 186297, 'Какую периодичность проверки соответствия схем электроснабжения фактическим эксплуатационным с отметкой на них о проверке обязан обеспечить ответственный за электрохозяйство?', '[\"Не реже 1 раза в год\",\"Не реже 1 раза в 2 года\",\"Не реже 1 раза в 3 года\",\"Не реже 1 раза в 5 лет\"]', '', 2, ''),
(311, 186298, 'Какие работы должны быть проведены в организации до вывода основного оборудования электроустановок в ремонт?', '[\"Нужно составить ведомости объема работ и смету, график ремонтных работ и подготовить техническую документацию\",\"Заготовить необходимые материалы и запасные части, привести в исправное состояние подручные инструменты и приспособления\",\"Подготовить рабочие места для ремонта, произвести планировку площадки с указанием размещения частей и деталей\",\"Укомплектовать и проинструктировать ремонтные бригады\",\"Необходимо провести все перечисленные подготовительные работы\"]', '', 5, ''),
(312, 186299, 'У какого количества опор воздушных линий, имеющих заземляющие устройства, производится выборочное вскрытие грунта для осмотра этих заземляющих устройств?', '[\"У 1 % опор\",\"У 2 % опор\",\"У 3 % опор\",\"У 5 % опор\"]', '', 2, ''),
(313, 186300, 'Что представляет собой система IT для электроустановок напряжением до 1 кВ?', '[\"Система, в которой нулевой защитный и нулевой рабочий проводники совмещены в одном проводнике на всем ее протяжении\",\"Система, в которой нейтраль источника питания глухо заземлена, а открытые проводящие части электроустановки присоединены к глухозаземленной нейтрали источника посредством нулевых защитных проводников\",\"Система, в которой нейтраль источника питания изолирована от земли, либо заземлена через приборы или устройства, имеющие большое сопротивление, а открытые проводящие части электроустановки заземлены\",\"Система, в которой нулевой защитный и нулевой рабочий проводники разделены на всем ее протяжении\"]', '', 3, ''),
(314, 186301, 'Какой тип опор устанавливается на прямых участках трассы воздушной линии электропередачи?', '[\"Промежуточные опоры\",\"Анкерные опоры\",\"Угловые опоры\",\"Концевые опоры\"]', '', 1, ''),
(315, 186302, 'Допускается ли использовать средства защиты с истекшим сроком годности?', '[\"Допускается\",\"Не допускается\",\"Допускается, при отсутствии внешних повреждений\",\"Допускается, с разрешения непосредственного руководителя\"]', '', 2, ''),
(316, 186303, 'Кто утверждает порядок проведения работы с персоналом в организации?', '[\"Руководитель организации или уполномоченное им должностное лицо организации\",\"Инспектор Ростехнадзора\",\"Ответственный за электрохозяйство Потребителя\",\"Федеральный орган исполнительной власти\"]', '', 1, ''),
(317, 186305, 'Допускается ли оформлять наряд-допуск в электронном виде?', '[\"Наряд-допуск может быть выписан только от руки на специальном бланке установленной формы\",\"Наряд-допуск допускается оформлять только в виде телефонограммы или радиограммы\",\"Допускается, по усмотрению руководителя, в зависимости от расположения диспетчерского пункта\",\"Разрешено оформлять наряд-допуск в электронном виде и передавать по электронной почте\"]', '', 4, ''),
(318, 186306, 'Кем выполняется подготовка рабочего места для выполнения строительно-монтажных работ?', '[\"Работниками организации - владельца электроустановки\",\"Работниками строительно-монтажной организации\",\"Работниками строительно-монтажной организации и организации - владельца электроустановок\"]', '', 1, ''),
(319, 186307, 'Какую периодичность пересмотра инструкций и схем обязан обеспечить ответственный за электрохозяйство?', '[\"Не реже 1 раза в год\",\"Не реже 1 раза в 2 года\",\"Не реже 1 раза в 3 года\",\"Не реже 1 раза в 5 лет\"]', '', 3, ''),
(320, 186308, 'В течение какого времени основное оборудование электроустановок, прошедшее капитальный ремонт, должно испытываться под нагрузкой?', '[\"В течение 12 часов\",\"В течение 24 часов\",\"В течение 36 часов\",\"В течение 48 часов\"]', '', 2, ''),
(321, 186309, 'Как определяется величина участка заземляющего устройства, подвергающегося выборочному вскрытию грунта?', '[\"Решением технического руководителя Потребителя\",\"Решением ответственного за электрохозяйство Потребителя\",\"Решением руководителя организации\",\"Величину участка определяет инспектор энергонадзора\"]', '', 1, ''),
(322, 186310, 'Что представляет собой система TT для электроустановок напряжением до 1 кВ?', '[\"Система, в которой нулевой защитный и нулевой рабочий проводники совмещены в одном проводнике на всем ее протяжении\",\"Система, в которой нулевой защитный и нулевой рабочий проводники разделены на всем ее протяжении\",\"Система, в которой нейтраль источника питания изолирована от земли, либо заземлена через приборы или устройства, имеющие большое сопротивление, а открытые проводящие части электроустановки заземлены\",\"Система, в которой нейтраль источника питания глухо заземлена, а открытые проводящие части электроустановки заземлены при помощи заземляющего устройства, электрически независимого от глухозаземленной нейтрали источника\"]', '', 4, ''),
(323, 186311, 'Допускается ли прохождение воздушной линии электропередачи (ВЛ) по территории стадионов, учебных и детских учреждений?', '[\"Не допускается\",\"Допускается\",\"Допускается при согласовании с территориальными органами Ростехнадзора\",\"Допускается при соответствующем обосновании в рабочей документации\"]', '', 1, ''),
(324, 186312, 'Какие из перечисленных электрозащитных средств и средств индивидуальной защиты не нумеруются для учета при вводе их в эксплуатацию?', '[\"Каски защитные, диэлектрические ковры, изолирующие подставки, плакаты безопасности, защитные ограждения, штанги для переноса и выравнивания потенциала\",\"Диэлектрические перчатки, галоши, боты\",\"Изолирующие накладки и колпаки\",\"Лестницы приставные и стремянки изолирующие стеклопластиковые\"]', '', 1, ''),
(325, 186313, 'Какие обязательные формы работы с персоналом не осуществляются для административно-технического персонала?', '[\"Предэкзаменационная подготовка и проверка знаний\",\"Производственный инструктаж\",\"Противоаварийные тренировки\"]', '', 3, ''),
(326, 186315, 'На какой срок разрешается выдавать наряд-допуск со дня начала работ в действующих электроустановках?', '[\"На срок не более 15 календарных дней\",\"На срок не более 10 календарных дней\",\"На срок не более 20 календарных дней\",\"На срок не более 25 календарных дней\"]', '', 1, ''),
(327, 186316, 'Кто определяет перечень профессий и рабочих мест, требующих отнесения производственного персонала к группе по электробезопасности I?', '[\"Технический руководитель Потребителя\",\"Руководитель организации (обособленного подразделения)\",\"Специалист по охране труда, контролирующий электроустановки\",\"Инспектор по энергетическому надзору\"]', '', 2, ''),
(328, 186317, 'За что несут персональную ответственность руководитель и специалисты энергетической службы?', '[\"За невыполнение требований, предусмотренных Правилами и должностными инструкциями\",\"За неправильную ликвидацию ими нарушений в работе электроустановок на обслуживаемом участке\",\"За нарушения в работе электроустановок из-за несвоевременного и неудовлетворительного технического обслуживания и невыполнения противоаварийных мероприятий\",\"За нарушения в эксплуатации электротехнологического оборудования\"]', '', 3, ''),
(329, 186318, 'Как часто должна проводиться проверка электрических схем электроустановок на соответствие фактическим эксплуатационным?', '[\"Не реже одного раза в полгода с отметкой о проверке\",\"Не реже одного раза в год с отметкой о проверке\",\"Не реже одного раза в два года с отметкой о проверке\",\"Не реже одного раза в три года с отметкой о проверке\",\"Не реже одного раза в пять лет с отметкой о проверке\"]', '', 3, ''),
(330, 186319, 'В каком случае элемент заземлителя должен быть заменен?', '[\"Если разрушено более 90 % его сечения\",\"Если разрушено более 80 % его сечения\",\"Если разрушено более 70 % его сечения\",\"Если разрушено более 60 % его сечения\",\"Если разрушено более 50 % его сечения\"]', '', 5, ''),
(331, 186320, 'Что является определением термина \"защита от прямого прикосновения\"?', '[\"Защита от поражения электрическим током при прикосновении к открытым проводящим частям, оказавшимся под напряжением, при повреждении изоляции\",\"Защита людей или животных от электрического контакта с открытыми проводящими частями\",\"Защита для предотвращения прикосновения к токоведущим частям, находящимся под напряжением\"]', '', 3, ''),
(332, 186321, 'Что, согласно Правилам устройства электроустановок, называется питающей осветительной сетью?', '[\"Сеть от распределительного устройства подстанции или ответвления от воздушных линий электропередачи до ВУ, ВРУ, ГРЩ\",\"Сеть от ВУ, ВРУ, ГРЩ до распределительных пунктов, щитков и пунктов питания наружного освещения\",\"Сеть от щитков до светильников, штепсельных розеток и других электроприемников\",\"Система, осуществляющая последовательное включение (отключение) участков групповой сети наружного освещения\"]', '', 1, ''),
(333, 186322, 'С какой периодичностью должны проверяться наличие и состояние средств защиты работником, ответственным за их состояние, с записью результатов осмотра в журнал?', '[\"Не реже 1 раза в 6 месяцев, а для переносных заземлений - не реже 1 раза в 3 месяца\",\"Для всех средств защиты 1 раз в 9 месяцев\",\"1 раз в 9 месяцев, а для переносных заземлений - 1 раз в 6 месяцев\",\"Только в ходе годовой инвентаризации материальных средств\"]', '', 1, ''),
(334, 186323, 'Какие обязательные формы работы с персоналом не осуществляются для ремонтного персонала?', '[\"Стажировка (включая обучение безопасным методам и приемам выполнения работ)\",\"Подготовка по новой должности (рабочему месту)\",\"Производственный инструктаж\",\"Противоаварийные тренировки\"]', '', 4, '');
